// Carbon Footprint Calculator
const energyData = $input.item(0).json;
const transportData = $input.item(1).json;

// Sample organizational data (in real scenario, this would come from your systems)
const organizationData = {
  electricity_consumption: 50000, // kWh/month
  natural_gas: 2000, // therms/month
  fleet_miles: 15000, // miles/month
  employee_commute: 25000, // miles/month
  air_travel: 50000, // miles/month
  employees: 100
};

function calculateCarbonFootprint(energyFactors, transportFactors, orgData) {
  const calculations = {
    scope1: {
      natural_gas: orgData.natural_gas * 11.7, // lbs CO2 per therm
      fleet_fuel: (orgData.fleet_miles / 25.4) * 19.6 // assuming 25.4 mpg
    },
    scope2: {
      electricity: orgData.electricity_consumption * 0.92 // lbs CO2 per kWh
    },
    scope3: {
      employee_commute: orgData.employee_commute * 0.77, // lbs CO2 per mile
      air_travel: orgData.air_travel * 0.53, // lbs CO2 per mile
      supply_chain: orgData.electricity_consumption * 0.1 // estimated
    }
  };

  const totalScope1 = Object.values(calculations.scope1).reduce((a, b) => a + b, 0);
  const totalScope2 = Object.values(calculations.scope2).reduce((a, b) => a + b, 0);
  const totalScope3 = Object.values(calculations.scope3).reduce((a, b) => a + b, 0);

  const totalEmissions = totalScope1 + totalScope2 + totalScope3;
  const emissionsPerEmployee = totalEmissions / orgData.employees;

  return {
    timestamp: new Date().toISOString(),
    total_emissions_lbs: Math.round(totalEmissions),
    total_emissions_tons: Math.round(totalEmissions / 2000 * 100) / 100,
    emissions_per_employee: Math.round(emissionsPerEmployee * 100) / 100,
    scope1_total: Math.round(totalScope1),
    scope2_total: Math.round(totalScope2),
    scope3_total: Math.round(totalScope3),
    breakdown: calculations,
    baseline_data: orgData
  };
}

const footprintResults = calculateCarbonFootprint(
  energyData.result || energyData,
  transportData.result || transportData,
  organizationData
);

return [{ json: footprintResults }];

// Reduction Opportunity Finder
const footprintData = $input.first().json;

function findReductionOpportunities(data) {
  const opportunities = [];
  const currentEmissions = data.total_emissions_tons;

  // Energy efficiency opportunities
  if (data.scope2_total > data.scope1_total * 0.5) {
    opportunities.push({
      category: 'Energy Efficiency',
      opportunity: 'LED lighting upgrade',
      potential_reduction_tons: Math.round(currentEmissions * 0.08 * 100) / 100,
      investment_required: '$25,000',
      payback_period: '2.5 years',
      priority: 'High',
      implementation_effort: 'Medium'
    });

    opportunities.push({
      category: 'Renewable Energy',
      opportunity: 'Solar panel installation',
      potential_reduction_tons: Math.round(currentEmissions * 0.25 * 100) / 100,
      investment_required: '$150,000',
      payback_period: '7 years',
      priority: 'High',
      implementation_effort: 'High'
    });
  }

  // Transportation opportunities
  if (data.breakdown.scope3.employee_commute > 5000) {
    opportunities.push({
      category: 'Transportation',
      opportunity: 'Remote work policy (3 days/week)',
      potential_reduction_tons: Math.round(currentEmissions * 0.12 * 100) / 100,
      investment_required: '$10,000',
      payback_period: '6 months',
      priority: 'High',
      implementation_effort: 'Low'
    });

    opportunities.push({
      category: 'Transportation',
      opportunity: 'Electric vehicle fleet transition',
      potential_reduction_tons: Math.round(currentEmissions * 0.15 * 100) / 100,
      investment_required: '$200,000',
      payback_period: '5 years',
      priority: 'Medium',
      implementation_effort: 'High'
    });
  }

  // Office efficiency
  opportunities.push({
    category: 'Office Operations',
    opportunity: 'Smart HVAC system',
    potential_reduction_tons: Math.round(currentEmissions * 0.06 * 100) / 100,
    investment_required: '$40,000',
    payback_period: '4 years',
    priority: 'Medium',
    implementation_effort: 'Medium'
  });

  const totalPotentialReduction = opportunities.reduce(
    (sum, opp) => sum + opp.potential_reduction_tons, 0
  );

  return {
    current_footprint: data,
    opportunities: opportunities,
    total_potential_reduction_tons: Math.round(totalPotentialReduction * 100) / 100,
    potential_reduction_percentage: Math.round((totalPotentialReduction / currentEmissions) * 100),
    analysis_date: new Date().toISOString()
  };
}

const reductionAnalysis = findReductionOpportunities(footprintData);

return [{ json: reductionAnalysis }];

// Sustainability Dashboard Data Formatter
const analysisData = $input.first().json;

function createDashboardData(data) {
  const footprint = data.current_footprint;
  const opportunities = data.opportunities;

  // KPI Cards Data
  const kpis = {
    total_emissions: {
      value: footprint.total_emissions_tons,
      unit: 'tons CO2e',
      trend: '+5.2%', // This would be calculated from historical data
      status: footprint.total_emissions_tons > 100 ? 'warning' : 'good'
    },
    emissions_per_employee: {
      value: footprint.emissions_per_employee,
      unit: 'lbs CO2e/employee',
      trend: '+2.1%',
      status: 'improving'
    },
    reduction_potential: {
      value: data.potential_reduction_percentage,
      unit: '%',
      trend: 'new',
      status: 'opportunity'
    },
    cost_savings_potential: {
      value: Math.round(data.total_potential_reduction_tons * 50), // $50 per ton estimate
      unit: '$/year',
      trend: 'projected',
      status: 'positive'
    }
  };

  // Scope Breakdown for Charts
  const scopeBreakdown = {
    labels: ['Scope 1 (Direct)', 'Scope 2 (Electricity)', 'Scope 3 (Indirect)'],
    data: [footprint.scope1_total, footprint.scope2_total, footprint.scope3_total],
    colors: ['#FF6B6B', '#4ECDC4', '#45B7D1']
  };

  // Monthly Trend (simulated - would be from historical data)
  const monthlyTrend = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    emissions: [85, 78, 92, 88, 95, footprint.total_emissions_tons],
    target: [80, 80, 80, 80, 80, 80]
  };

  // Top Opportunities for Action Items
  const topOpportunities = opportunities
    .sort((a, b) => b.potential_reduction_tons - a.potential_reduction_tons)
    .slice(0, 5)
    .map(opp => ({
      ...opp,
      impact_score: Math.round((opp.potential_reduction_tons / data.total_potential_reduction_tons) * 100)
    }));

  return {
    dashboard_data: {
      kpis: kpis,
      scope_breakdown: scopeBreakdown,
      monthly_trend: monthlyTrend,
      top_opportunities: topOpportunities,
      last_updated: new Date().toISOString(),
      next_update: new Date(Date.now() + 24*60*60*1000).toISOString()
    },
    raw_analysis: data
  };
}

const dashboardData = createDashboardData(analysisData);

return [{ json: dashboardData }];

// ESG Report Generator
const dashboardData = $input.first().json;
const data = dashboardData.raw_analysis;
const kpis = dashboardData.dashboard_data.kpis;

function generateESGReport(analysisData, kpiData) {
  const reportDate = new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  const executiveSummary = `
**EXECUTIVE SUMMARY**

Our organization's current carbon footprint stands at ${analysisData.current_footprint.total_emissions_tons} tons CO2e, with emissions per employee at ${analysisData.current_footprint.emissions_per_employee} lbs CO2e. 

We have identified ${analysisData.opportunities.length} key reduction opportunities that could decrease our emissions by ${analysisData.potential_reduction_percentage}% (${analysisData.total_potential_reduction_tons} tons CO2e annually).

**KEY FINDINGS:**
‚Ä¢ Scope 2 emissions (electricity) represent ${Math.round((analysisData.current_footprint.scope2_total / analysisData.current_footprint.total_emissions_lbs) * 100)}% of total emissions
‚Ä¢ Transportation accounts for ${Math.round(((analysisData.current_footprint.breakdown.scope3.employee_commute + analysisData.current_footprint.breakdown.scope1.fleet_fuel) / analysisData.current_footprint.total_emissions_lbs) * 100)}% of our footprint
‚Ä¢ High-impact, low-cost opportunities exist in remote work policies and energy efficiency
  `;

  const emissionsBreakdown = `
**EMISSIONS BREAKDOWN**

**Scope 1 (Direct Emissions): ${Math.round(analysisData.current_footprint.scope1_total/2000*100)/100} tons CO2e**
‚Ä¢ Natural Gas: ${Math.round(analysisData.current_footprint.breakdown.scope1.natural_gas)} lbs CO2e
‚Ä¢ Fleet Vehicles: ${Math.round(analysisData.current_footprint.breakdown.scope1.fleet_fuel)} lbs CO2e

**Scope 2 (Indirect - Electricity): ${Math.round(analysisData.current_footprint.scope2_total/2000*100)/100} tons CO2e**
‚Ä¢ Purchased Electricity: ${Math.round(analysisData.current_footprint.breakdown.scope2.electricity)} lbs CO2e

**Scope 3 (Other Indirect): ${Math.round(analysisData.current_footprint.scope3_total/2000*100)/100} tons CO2e**
‚Ä¢ Employee Commuting: ${Math.round(analysisData.current_footprint.breakdown.scope3.employee_commute)} lbs CO2e
‚Ä¢ Business Travel: ${Math.round(analysisData.current_footprint.breakdown.scope3.air_travel)} lbs CO2e
‚Ä¢ Supply Chain: ${Math.round(analysisData.current_footprint.breakdown.scope3.supply_chain)} lbs CO2e
  `;

  const opportunitiesSection = analysisData.opportunities.map(opp => 
    `‚Ä¢ **${opp.opportunity}** (${opp.category})\n  Reduction: ${opp.potential_reduction_tons} tons CO2e | Investment: ${opp.investment_required} | Priority: ${opp.priority}`
  ).join('\n\n');

  const recommendations = `
**STRATEGIC RECOMMENDATIONS**

**Immediate Actions (0-6 months):**
1. Implement remote work policy (3 days/week) - High impact, low cost
2. Upgrade to LED lighting across all facilities
3. Establish employee sustainability awareness program

**Medium-term Goals (6-18 months):**
1. Install smart HVAC systems with automated controls
2. Conduct comprehensive energy audit of all facilities
3. Develop supplier sustainability scorecard

**Long-term Commitments (1-3 years):**
1. Transition to renewable energy sources (solar installation)
2. Electrify vehicle fleet where feasible
3. Achieve carbon neutrality through verified offsets

**Financial Impact:**
Total estimated annual savings from all initiatives: $${Math.round(analysisData.total_potential_reduction_tons * 50).toLocaleString()}
Payback period for major investments: 3-7 years
  `;

  const fullReport = `
# üå± CARBON FOOTPRINT & ESG REPORT
**Generated: ${reportDate}**
**Reporting Period: Current Month**
**Organization: [Company Name]**

${executiveSummary}

${emissionsBreakdown}

**REDUCTION OPPORTUNITIES**

${opportunitiesSection}

${recommendations}

**COMPLIANCE & BENCHMARKING**
‚Ä¢ Current emissions intensity: ${analysisData.current_footprint.emissions_per_employee} lbs CO2e per employee
‚Ä¢ Industry benchmark: 1,200-1,800 lbs CO2e per employee (service sector)
‚Ä¢ Science-based target alignment: Reduction pathway defined for 1.5¬∞C scenario

**NEXT STEPS**
1. Present findings to executive leadership
2. Allocate budget for priority initiatives
3. Establish monthly monitoring and reporting cadence
4. Engage employees in sustainability initiatives

---
*This report was automatically generated using real-time data collection and analysis. For questions or detailed implementation planning, contact the Sustainability Team.*
  `;

  return {
    report_text: fullReport,
    report_date: reportDate,
    report_type: 'Carbon Footprint & ESG Analysis',
    key_metrics: {
      total_emissions: analysisData.current_footprint.total_emissions_tons,
      reduction_potential: analysisData.potential_reduction_percentage,
      cost_savings_potential: Math.round(analysisData.total_potential_reduction_tons * 50),
      opportunities_count: analysisData.opportunities.length
    },
    file_name: `Carbon_Footprint_Report_${new Date().toISOString().split('T')[0]}.md`
  };
}

const esgReport = generateESGReport(data, kpis);

return [{ json: esgReport }];
