// Get binary data from previous node
const binaryData = $input.first().binary?.data;

if (!binaryData) {
  throw new Error("No binary data found");
}

// Binary data in n8n is already base64 encoded
const base64String = binaryData.data;

return {
  json: {
    base64_content: base64String,
    // Add other fields as needed
  }
};
