// HTML Extract result
const scraped = items[0].json;

// Original data from Wait node
const original = $items("Pause Between Requests")[0].json;

// Clean and parse price
const priceNum = parseFloat((scraped.current_price || "").replace(/[^0-9.]+/g, ""));

// Original price from Google Sheet
const lastPrice = parseFloat(original.price);

return [{
  json: {
    product_url: original.product_url,
    row_number: original.row_number,
    last_price: lastPrice,
    current_price: priceNum
  }
}];

// Get the incoming item
const item = items[0].json;

// Compute whether the price changed
const priceChanged = item.last_price !== item.current_price;

// Calculate percentage difference (will be NaN if last_price is null/0)
let priceDiffPct = null;
if (item.last_price && item.last_price !== 0) {
  priceDiffPct = (
    ((item.current_price - item.last_price) / item.last_price) *
    100
  ).toFixed(2);
}

// Return everything, including product_url and last_price
return [
  {
    json: {
      product_url: item.product_url,
      last_price: item.last_price,
      current_price: item.current_price,
      price_changed: priceChanged,
      price_diff_pct: priceDiffPct !== null ? parseFloat(priceDiffPct) : null,
      timestamp: new Date().toISOString(),
    },
  },
];

// Function node: 'items' is an array of incoming items
if (!items.length) {
  return [];
}

const input = items[0].json;
const cleaned = {};

for (const key in input) {
  const cleanKey = key.replace(/\t/g, '').trim();
  cleaned[cleanKey] = input[key];
}

return [
  {
    json: {
      product_url: cleaned.product_url,
      last_price: cleaned.last_price,
      current_price: cleaned.current_price,
      price_changed: cleaned.price_changed,
      price_diff_pct: cleaned.price_diff_pct,
      timestamp: cleaned.timestamp,
    },
  },
];

const itemsFormatted = [];

for (const item of items) {
  const data = item.json;

  // Determine alert type
  const isDrop = data.price_diff_pct < 0;
  const alertType = isDrop ? "üìâ *Price Drop Alert*" : "üìà *Price Hike Alert*";

  // Format timestamp to IST
  const date = new Date(data.timestamp);
  const istOffset = 5.5 * 60 * 60 * 1000;
  const istDate = new Date(date.getTime() + istOffset);
  const formattedDate = istDate.toLocaleString("en-IN", {
    day: "2-digit",
    month: "short",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    hour12: true
  });

  // Build message
  const message = `${alertType}

üõçÔ∏è *Product:* (${data.product_url})
üí∏ *Last Price:* $${data.last_price}
üí∞ *Current Price:* $${data.current_price}
üìä *Change:* ${data.price_diff_pct > 0 ? "+" : ""}${data.price_diff_pct}%
üïí *Time:* ${formattedDate}`;

  itemsFormatted.push({ json: { message } });
}

return itemsFormatted;

