// NODE 1: Extract All Model Names
// Finds all unique model names in workflow execution data

const findAllModels = (data) => {
  const models = new Set();
  const execs = Array.isArray(data) ? data : [data];

  execs.forEach(exec => {
    const runData = exec?.data?.resultData?.runData || {};
    Object.values(runData).forEach(runs => {
      if (!Array.isArray(runs)) return;
      runs.forEach(run => {
        // Check all possible locations for model names
        const locations = [
          run.inputOverride?.ai_languageModel?.[0]?.[0]?.json?.options?.model,
          run.data?.ai_languageModel?.[0]?.[0]?.json?.options?.model,
          run.parameters?.model?.value,
          run.parameters?.model,
          run.options?.model
        ];

        locations.forEach(model => {
          if (model && typeof model === 'string') {
            models.add(model);
          }
        });
      });
    });
  });

  return Array.from(models);
};

// Process input and collect all unique models
const allModels = new Set();

for (const item of $input.all()) {
  const models = findAllModels(item.json);
  models.forEach(model => allModels.add(model));
}

// Return single item with models_used list
return [{
  json: {
    models_used: Array.from(allModels),
    model_count: allModels.size,
    original_data: $input.all()[0].json
  }
}];

// NODE 2: Validate Models Against Dictionaries

const item = $input.first().json;
const models_used = item.models_used || [];
const standardize_names_dic = item.standardize_names_dic || {};
const model_price_dic = item.model_price_dic || {};

// Check which models are missing from each dictionary
const missing_from_names = models_used.filter(model => !standardize_names_dic[model]);
const missing_from_prices = models_used.filter(model => !model_price_dic[model]);

// Build result
if (missing_from_names.length === 0 && missing_from_prices.length === 0) {
  return [{
    json: {
      passed: true,
      message: "All models validated successfully",
      models_used: models_used,
      standardize_names_dic: standardize_names_dic,
      model_price_dic: model_price_dic,
      original_data: item.original_data
    }
  }];
} else {
  const errors = [];
  if (missing_from_names.length > 0) {
    errors.push(`Missing from standardize_names_dic: ${missing_from_names.join(', ')}`);
  }
  if (missing_from_prices.length > 0) {
    errors.push(`Missing from model_price_dic: ${missing_from_prices.join(', ')}`);
  }

  return [{
    json: {
      passed: false,
      message: errors.join(' | '),
      missing_from_names: missing_from_names,
      missing_from_prices: missing_from_prices,
      models_used: models_used
    }
  }];
}

