// NODE 3: Extract LLM Usage (without cost calculation and summary)

const extractLLMUsageData = (data) => {
  const execs = Array.isArray(data) ? data : [data];
  const usages = [];

  execs.forEach(exec => {
    const runData = exec?.data?.resultData?.runData || {};
    const workflowName = exec?.workflowData?.name || 'unknown';
    const workflowId = exec?.workflowId || 'unknown';
    const executionId = exec?.id || 'unknown';
    const createdAt = exec?.createdAt || null;
    const executionStatus = exec?.status || 'unknown';

    Object.entries(runData).forEach(([node, runs]) => {
      if (!Array.isArray(runs)) return;
      runs.forEach(run => {
        // Find token usage in multiple locations
        let tokens = run.data?.ai_languageModel?.[0]?.[0]?.json?.tokenUsage || 
                     run.tokenUsage || null;

        if (tokens?.promptTokens || tokens?.completionTokens) {
          // Extract comprehensive model info
          const inputOverride = run.inputOverride?.ai_languageModel?.[0]?.[0]?.json || {};
          const options = inputOverride.options || {};
          let model = options.model || 'unknown';

          // Extract the actual messages/prompts if available
          const messages = inputOverride.messages || [];
          const promptPreview = messages.length > 0 ? 
            messages[0].substring(0, 100) + (messages[0].length > 100 ? '...' : '') : '';

          // Extract response info
          const response = run.data?.ai_languageModel?.[0]?.[0]?.json?.response?.generations?.[0]?.[0] || {};
          const finishReason = response.generationInfo?.finish_reason || 'unknown';
          const responsePreview = response.text ? 
            response.text.substring(0, 100) + (response.text.length > 100 ? '...' : '') : '';

          // Extract execution metadata
          const startTime = run.startTime || null;
          const executionIndex = run.executionIndex || 0;
          const nodeExecutionStatus = run.executionStatus || 'unknown';

          // Extract previous nodes chain
          const previousNodes = [];
          if (run.source && Array.isArray(run.source)) {
            run.source.forEach(src => {
              if (src.previousNode) previousNodes.push(src.previousNode);
            });
          }

          // Extract temperature and other model parameters
          const temperature = options.temperature || options.model_kwargs?.temperature || null;
          const maxTokens = options.max_tokens || options.maxTokens || null;
          const timeout = options.timeout || null;
          const maxRetries = options.max_retries || 0;

          // Extract any session/memory IDs
          const sessionId = run.metadata?.sessionId || null;

          usages.push({
            // Execution context
            workflowName,
            workflowId,
            executionId,
            createdAt,
            executionStatus,

            // Node info
            node,
            nodeExecutionStatus,
            executionIndex,

            // Model & tokens
            model,
            promptTokens: tokens.promptTokens || 0,
            completionTokens: tokens.completionTokens || 0,
            totalTokens: tokens.totalTokens || 0,

            // Performance
            executionTime: run.executionTime || 0,
            startTime,

            // Model parameters
            temperature,
            maxTokens,
            timeout,
            maxRetries,
            finishReason,

            // Context
            previousNodes: previousNodes.join(' â†’ ') || 'Start',
            sessionId,

            // Content previews (useful for debugging)
            promptPreview,
            responsePreview,

            // Estimated tokens if not provided
            estimatedTokens: inputOverride.estimatedTokens || null
          });
        }
      });
    });
  });
  return usages;
};

// Process each input item
const results = [];
for (const item of $input.all()) {
  const llmUsages = extractLLMUsageData(item.json);

  // Add each usage as a separate output item
  llmUsages.forEach(usage => {
    results.push({
      json: usage
    });
  });

  // If no LLM usage found, still return the execution info
  if (llmUsages.length === 0) {
    results.push({
      json: {
        executionId: item.json.id || 'unknown',
        workflowId: item.json.workflowId || 'unknown',
        message: 'No LLM usage detected in this execution',
        totalTokens: 0
      }
    });
  }
}

return results;

// NODE 4: Calculate Costs and Add Summary

// Get dictionaries from first item and LLM usages from rest
const configItem = $input.all().find(item => item.json.passed === true) || $input.first();
const standardize_names_dic = configItem.json.standardize_names_dic || {};
const model_price_dic = configItem.json.model_price_dic || {};

// Filter out config item and get only LLM usage items
const llmUsages = $input.all().filter(item => 
  item.json.promptTokens !== undefined && 
  item.json.completionTokens !== undefined
);

// Process each usage with costs
const results = llmUsages.map(item => {
  const usage = item.json;

  // Standardize model name
  const standardModel = standardize_names_dic[usage.model] || usage.model;

  // Get pricing (prices are per 1M tokens with "input"/"output" keys)
  const pricing = model_price_dic[standardModel] || { input: 0, output: 0 };

  // Calculate costs (divide by 1,000,000 since prices are per million)
  const promptCost = (usage.promptTokens / 1000000) * pricing.input;
  const completionCost = (usage.completionTokens / 1000000) * pricing.output;
  const totalCost = promptCost + completionCost;

  return {
    json: {
      ...usage,
      standardModel,
      promptCostUSD: promptCost.toFixed(8),
      completionCostUSD: completionCost.toFixed(8),
      totalCostUSD: totalCost.toFixed(8),
      pricePerMPrompt: pricing.input,
      pricePerMCompletion: pricing.output
    }
  };
});

// Calculate summary statistics if we have results
if (results.length > 0) {
  const summary = {
    isSummary: true,
    totalExecutions: results.length,
    totalPromptTokens: 0,
    totalCompletionTokens: 0,
    totalTokens: 0,
    totalPromptCostUSD: 0,
    totalCompletionCostUSD: 0,
    totalCostUSD: 0,
    totalExecutionTimeMs: 0,
    byModel: {},
    byNode: {}
  };

  // Calculate totals
  results.forEach(r => {
    summary.totalPromptTokens += r.json.promptTokens;
    summary.totalCompletionTokens += r.json.completionTokens;
    summary.totalTokens += r.json.totalTokens;
    summary.totalPromptCostUSD += parseFloat(r.json.promptCostUSD);
    summary.totalCompletionCostUSD += parseFloat(r.json.completionCostUSD);
    summary.totalCostUSD += parseFloat(r.json.totalCostUSD);
    summary.totalExecutionTimeMs += r.json.executionTime;

    // Group by model
    const model = r.json.standardModel;
    if (!summary.byModel[model]) {
      summary.byModel[model] = {
        count: 0,
        promptTokens: 0,
        completionTokens: 0,
        totalTokens: 0,
        totalCostUSD: 0
      };
    }
    summary.byModel[model].count++;
    summary.byModel[model].promptTokens += r.json.promptTokens;
    summary.byModel[model].completionTokens += r.json.completionTokens;
    summary.byModel[model].totalTokens += r.json.totalTokens;
    summary.byModel[model].totalCostUSD += parseFloat(r.json.totalCostUSD);

    // Group by node
    const node = r.json.node;
    if (!summary.byNode[node]) {
      summary.byNode[node] = {
        count: 0,
        totalTokens: 0,
        totalCostUSD: 0
      };
    }
    summary.byNode[node].count++;
    summary.byNode[node].totalTokens += r.json.totalTokens;
    summary.byNode[node].totalCostUSD += parseFloat(r.json.totalCostUSD);
  });

  // Format costs
  summary.totalPromptCostUSD = summary.totalPromptCostUSD.toFixed(8);
  summary.totalCompletionCostUSD = summary.totalCompletionCostUSD.toFixed(8);
  summary.totalCostUSD = summary.totalCostUSD.toFixed(8);
  summary.avgCostPerCall = (parseFloat(summary.totalCostUSD) / summary.totalExecutions).toFixed(8);
  summary.avgTokensPerCall = Math.round(summary.totalTokens / summary.totalExecutions);

  // Format byModel costs
  Object.keys(summary.byModel).forEach(model => {
    summary.byModel[model].totalCostUSD = summary.byModel[model].totalCostUSD.toFixed(8);
  });

  // Format byNode costs
  Object.keys(summary.byNode).forEach(node => {
    summary.byNode[node].totalCostUSD = summary.byNode[node].totalCostUSD.toFixed(8);
  });

  // Add summary as last item
  results.push({ json: summary });
}

return results.length > 0 ? results : [{ 
  json: { 
    message: "No LLM usage data found to calculate costs" 
  } 
}];
