// n8n code to aggregate all Google Analytics data into a single block for the LangChain agent

// Get all input data from the Google Analytics node
const inputData = $input.all();

// Function to format numbers with thousand separators
function formatNumber(num) {
  return parseInt(num).toLocaleString('en-US');
}

// Function to extract the domain from a URL
function extractDomain(url) {
  if (!url) return 'unknown';
  try {
    const cleanUrl = url.replace(/^https?:\/\//, '').replace(/^www\./, '');
    const domain = cleanUrl.split('/')[0];
    return domain;
  } catch (error) {
    return 'unknown';
  }
}

// Aggregate all data
const allData = inputData.map(item => item.json);

// Calculate totals and global metrics
const aggregatedMetrics = {
  totalSessions: allData.reduce((sum, item) => sum + parseInt(item.sessions || 0), 0),
  totalUsers: allData.reduce((sum, item) => sum + parseInt(item.totalUsers || 0), 0),
  totalPageViews: allData.reduce((sum, item) => sum + parseInt(item.screenPageViews || 0), 0),
  countries: [...new Set(allData.map(item => item.country).filter(Boolean))],
  urls: [...new Set(allData.map(item => item.pageLocation).filter(Boolean))],
  dataCount: allData.length
};

// Calculate derived metrics
aggregatedMetrics.avgPageViewsPerSession = (aggregatedMetrics.totalPageViews / aggregatedMetrics.totalSessions).toFixed(2);
aggregatedMetrics.avgSessionsPerUser = (aggregatedMetrics.totalSessions / aggregatedMetrics.totalUsers).toFixed(2);
aggregatedMetrics.returnUserRate = ((aggregatedMetrics.totalSessions - aggregatedMetrics.totalUsers) / aggregatedMetrics.totalSessions * 100).toFixed(1);

// Create the comprehensive text summary
const comprehensiveReport = `
=== GOOGLE ANALYTICS REPORT - LAST 30 DAYS ===

üìä GLOBAL METRICS:
‚Ä¢ Total sessions: ${formatNumber(aggregatedMetrics.totalSessions)}
‚Ä¢ Unique users: ${formatNumber(aggregatedMetrics.totalUsers)}
‚Ä¢ Total page views: ${formatNumber(aggregatedMetrics.totalPageViews)}
‚Ä¢ Page views per session: ${aggregatedMetrics.avgPageViewsPerSession}
‚Ä¢ Sessions per user: ${aggregatedMetrics.avgSessionsPerUser}
‚Ä¢ Returning user rate: ${aggregatedMetrics.returnUserRate}%

üåç GEOGRAPHICAL BREAKDOWN:
‚Ä¢ Number of countries: ${aggregatedMetrics.countries.length}
‚Ä¢ Main countries: ${aggregatedMetrics.countries.join(', ')}

üîó WEBSITES ANALYZED:
‚Ä¢ Number of URLs: ${aggregatedMetrics.urls.length}
‚Ä¢ Main URLs: ${aggregatedMetrics.urls.join(', ')}
`.trim();

// Simplified return with a single "content" field
return [{
  json: {
    content: comprehensiveReport
  }
}];

