// Formatting for Search Console with handling of empty data
const data = $input.first().json;

// Check if we have actual data
const hasData = data.rows && data.rows.length > 0;

if (!hasData) {
  // Case for a new/recent site - Constructive response
  const reportText = `
=== GOOGLE SEARCH CONSOLE REPORT - TARGET WEBSITE ===
üìä ANALYSIS DATE: ${new Date().toLocaleDateString('en-US')}

üÜï STATUS: SITE IN SEO START-UP PHASE
‚Ä¢ No Search Console data available (this is normal for a new site).
‚Ä¢ API functional: ‚úÖ Authentication successful.

üöÄ POSITIVE DIAGNOSIS:
‚Ä¢ Technical setup: Correct.
‚Ä¢ Google API access: Validated.
‚Ä¢ Indexing: In progress.

üéØ RECOMMENDATIONS TO SPEED UP INDEXING:
‚Ä¢ Submit the XML sitemap to Google Search Console.
‚Ä¢ Create high-quality content regularly.
‚Ä¢ Obtain natural backlinks.
‚Ä¢ Optimize meta descriptions and titles.
‚Ä¢ Use Google Search Console to request manual indexing.

‚è≥ EXPECTED TIMELINE:
‚Ä¢ First GSC data: 2-4 weeks after indexing.
‚Ä¢ Significant data: 2-3 months with regular content.
‚Ä¢ Mature SEO performance: 6-12 months.
`;

  return [{
    json: {
      content: reportText,
      searchConsoleData: {
        status: "new_site",
        hasData: false,
      }
    }
  }];
}

// If we have data
const searchData = data.rows.map(row => ({
  query: row.keys[0] || 'Not specified',
  clicks: row.clicks || 0,
  impressions: row.impressions || 0,
  ctr: ((row.ctr || 0) * 100).toFixed(2),
  position: (row.position || 0).toFixed(1)
}));

const totalClicks = searchData.reduce((sum, item) => sum + item.clicks, 0);
const totalImpressions = searchData.reduce((sum, item) => sum + item.impressions, 0);

const reportText = `
=== GOOGLE SEARCH CONSOLE REPORT - DATA AVAILABLE ===
üìä METRICS: ${totalClicks} clicks, ${totalImpressions} impressions.
üîù TOP QUERIES: ${searchData.slice(0, 5).map(item => item.query).join(', ')}
`;

return [{
  json: {
    content: reportText,
    searchConsoleData: {
      status: "data_available",
      hasData: true,
      totalClicks,
      totalImpressions,
      topQueries: searchData.slice(0, 5)
    }
  }
}];

// Advanced formatting of Page Speed Insights data
const data = $input.first().json;

// Verify that the data is valid
if (!data.lighthouseResult) {
  return [{
    json: {
      error: "Invalid Page Speed Insights data",
      content: "Error while fetching performance metrics"
    }
  }];
}

const lighthouse = data.lighthouseResult;

// Generate the text report for the AI agent
const reportText = `
=== PAGE SPEED INSIGHTS REPORT - TARGET WEBSITE ===
üìä ANALYSIS DATE: ${new Date().toLocaleDateString('en-US')} - Strategy: ${lighthouse.configSettings.emulatedFormFactor}

üéØ LIGHTHOUSE SCORES:
‚Ä¢ Performance: ${Math.round(lighthouse.categories.performance.score * 100)}/100
‚Ä¢ Accessibility: ${Math.round(lighthouse.categories.accessibility.score * 100)}/100
‚Ä¢ Best Practices: ${Math.round(lighthouse.categories['best-practices'].score * 100)}/100
‚Ä¢ SEO: ${Math.round(lighthouse.categories.seo.score * 100)}/100

‚ö° CORE WEB VITALS:
‚Ä¢ First Contentful Paint: ${lighthouse.audits['first-contentful-paint'].displayValue}
‚Ä¢ Largest Contentful Paint: ${lighthouse.audits['largest-contentful-paint'].displayValue}
‚Ä¢ Cumulative Layout Shift: ${lighthouse.audits['cumulative-layout-shift'].displayValue}
‚Ä¢ Speed Index: ${lighthouse.audits['speed-index'].displayValue}
`;

return [{
  json: {
    content: reportText
  }
}];

// Complete extraction of SEO data from HTML
const html = $input.first().json.body || $input.first().json.data || '';

if (!html) {
  return [{
    json: {
      error: "No HTML content retrieved",
      content: "Error while scraping the site"
    }
  }];
}

function cleanText(text) {
  return text ? text.replace(/<[^>]*>/g, '').trim() : '';
}

const seoExtraction = {
  title: cleanText(html.match(/<title[^>]*>(.*?)<\/title>/i)?.[1]) || 'Not found',
  metaDescription: html.match(/<meta[^>]*name=["']description["'][^>]*content=["']([^"']*)["'][^>]*>/i)?.[1] || 'Not found',
  h1Tags: (html.match(/<h1[^>]*>(.*?)<\/h1>/gi) || []).map(h1 => cleanText(h1)),
  totalImages: (html.match(/<img[^>]*>/gi) || []).length,
  imagesWithAlt: (html.match(/<img[^>]*alt=["'][^"']*["'][^>]*>/gi) || []).length,
};

seoExtraction.imagesWithoutAlt = seoExtraction.totalImages - seoExtraction.imagesWithAlt;

return [{
  json: seoExtraction
}];

// Complete analysis and scoring of extracted SEO data
const seoData = $input.first().json;

if (!seoData || !seoData.title) {
  return [{
    json: {
      error: "Missing SEO data",
      content: "Unable to proceed with the audit"
    }
  }];
}

// SEO scoring system (out of 100 points)
const scoring = {
  technical: 0,
  onPage: 0,
};

// TECHNICAL SCORE
if (seoData.title && seoData.title !== 'Not found') scoring.technical += 10;
if (seoData.metaDescription && seoData.metaDescription !== 'Not found') scoring.technical += 10;

// ON-PAGE SCORE
if (seoData.h1Tags.length === 1) scoring.onPage += 10;
if (seoData.imagesWithoutAlt === 0 && seoData.totalImages > 0) scoring.onPage += 10;

const totalScore = scoring.technical + scoring.onPage;
let scoreStatus = '';
if (totalScore >= 80) scoreStatus = 'EXCELLENT';
else if (totalScore >= 60) scoreStatus = 'GOOD';
else if (totalScore >= 40) scoreStatus = 'AVERAGE';
else scoreStatus = 'POOR';

const criticalIssues = [];
if (!seoData.title || seoData.title === 'Not found') criticalIssues.push('‚ùå CRITICAL: Missing title');
if (!seoData.metaDescription || seoData.metaDescription === 'Not found') criticalIssues.push('‚ùå CRITICAL: Missing meta description');
if (seoData.h1Tags.length === 0) criticalIssues.push('‚ùå CRITICAL: No H1 tag detected');
else if (seoData.h1Tags.length > 1) criticalIssues.push('‚ö†Ô∏è IMPORTANT: Multiple H1 tags detected');

const recommendations = [];
if (seoData.imagesWithoutAlt > 0) recommendations.push(`üñºÔ∏è Add alt text to ${seoData.imagesWithoutAlt} images`);

// Complete audit report
const auditReport = `
=== COMPLETE TECHNICAL SEO AUDIT - TARGET WEBSITE ===
üìä ANALYSIS DATE: ${new Date().toLocaleDateString('en-US')}

‚òÖ GLOBAL SEO SCORE: ${totalScore}/100 (${scoreStatus})

üìã ON-PAGE ANALYSIS:
‚Ä¢ Title: ${seoData.title !== 'Not found' ? '‚úÖ ' + seoData.title.substring(0, 80) : '‚ùå Missing'}
‚Ä¢ Meta description: ${seoData.metaDescription !== 'Not found' ? '‚úÖ ' + seoData.metaDescription.substring(0, 100) : '‚ùå Missing'}
‚Ä¢ H1: ${seoData.h1Tags.length} found(s) ${seoData.h1Tags.length === 1 ? '‚úÖ' : '‚ö†Ô∏è'}
‚Ä¢ Images with alt text: ${seoData.imagesWithAlt}/${seoData.totalImages} ${seoData.imagesWithoutAlt === 0 ? '‚úÖ' : '‚ùå'}

${criticalIssues.length > 0 ? 'üö® CRITICAL ISSUES:\n' + criticalIssues.join('\n') : ''}
${recommendations.length > 0 ? 'üí° PRIORITY RECOMMENDATIONS:\n' + recommendations.map(rec => '‚Ä¢ ' + rec).join('\n') : ''}
`;

return [{
  json: {
    content: auditReport,
    auditData: {
      totalScore,
      scoreStatus,
      scoring,
      criticalIssues,
      recommendations,
    }
  }
}];

// Simple aggregator - post-merge
const allInputs = $input.all();

// Extract contents
const reports = allInputs.map((input, index) => ({
  index: index + 1,
  content: input.json.output || input.json.content || JSON.stringify(input.json),
}));

// Create the consolidated text
const consolidatedText = `
=== CONSOLIDATED DATA FOR MASTER ANALYST ===
üìä Reports received: ${reports.length}/4

üìÑ REPORT 1 (ANALYTICS):
${reports[0]?.content.substring(0, 1000)}...

üìÑ REPORT 2 (PAGESPEED):
${reports[1]?.content.substring(0, 1000)}...

üìÑ REPORT 3 (SEARCH CONSOLE):
${reports[2]?.content.substring(0, 1000)}...

üìÑ REPORT 4 (TECHNICAL AUDIT):
${reports[3]?.content.substring(0, 1000)}...

üéØ MASTER ANALYST INSTRUCTIONS:
Analyze these 4 reports and create a comprehensive SEO summary with a prioritized action plan.
`;

return [{
  json: {
    textSummary: consolidatedText
  }
}];
