value.ADDRESS:
={{ (v => {
  const S = x =>
    x == null ? '' :
    Array.isArray(x)
      ? x.map(S).map(s => String(s).trim()).filter(Boolean).join(',\n')
      : typeof x === 'object'
        ? Object.entries(x)
            .filter(([_, val]) => {
              if (val == null) return false
              if (typeof val === 'string') return val.trim() !== ''
              if (Array.isArray(val)) return val.map(S).some(s => String(s).trim() !== '')
              if (typeof val === 'object') return Object.keys(val).length > 0
              return true
            })
            .map(([k, val]) => {
              const sv = S(val)
              return sv ? `${k}: ${sv}` : ''
            })
            .filter(Boolean)
            .join(',\n')
        : String(x).trim()
  return S(v)
})($json.response?.title_block?.address) }}

