function toICSDate(query) {
  let d;

  if (query instanceof Date) {
    d = query;

  } else if (typeof query === 'string') {
    if (/^\d{8}T\d{6}Z$/.test(query)) {
      // Already ICS UTC format
      return query;
    } else if (/^\d{8}T\d{6}$/.test(query)) {
      // Compact local format, parse manually
      const [datePart, timePart] = query.split('T');
      const year   = +datePart.slice(0, 4);
      const month  = +datePart.slice(4, 6) - 1; // 0‑based
      const day    = +datePart.slice(6, 8);
      const hour   = +timePart.slice(0, 2);
      const minute = +timePart.slice(2, 4);
      const second = +timePart.slice(4, 6);
      d = new Date(year, month, day, hour, minute, second);
    } else {
      // Assume ISO‑8601 string ("2025-09-26T11:00:00-04:00")
      d = new Date(query);
    }
  }

  if (!(d instanceof Date) || isNaN(d)) {
    throw new Error('Invalid input: must be Date, ISO, ICS UTC, or compact string');
  }

  // Always format UTC
  return d.toISOString()
          .replace(/[-:]/g, '')   // strip separators
          .replace(/\.\d{3}Z$/, 'Z'); // remove ms
}

// Export
return toICSDate(query);

