const urlArray = [];

for (const item of items) {
  const data = item.json;

  if (data.output) {
    urlArray.push({
      motionMP4URL: data.output, // This is now the actual video URL
      imageId: data.id || null,
      createdAt: data.createdAt || null
    });
  }
}

return [{
  json: {
    urls: urlArray
  }
}];
