const html = items[0].json.output;
const h1Match = html.match(/<h1>(.*?)<\/h1>/i);
const title = h1Match ? h1Match[1] : '';
const body = html.replace(h1Match[0], '').trim();

return [
  {
    json: {
      title,
      body,
    },
  },
];

const data = items[0].json;
const output = [];
for (const [title, search] of Object.entries(data)) {
  output.push({
    json: {
      title,
      search
    }
  });
}

return output;

function score(stat) {
  const views = Number(stat.viewCount) || 0;
  const likes = Number(stat.likeCount) || 0;
  const comments = Number(stat.commentCount) || 0;
  return comments + (likes * 10) + (views * 20);
}

items.sort((a, b) => {
  return score(b.json.statistics) - score(a.json.statistics);
});

return [items[0]];
