function extractSitemapUrl(robotsTxt) {
  // Buscamos l√≠nea que empiece con "Sitemap:" (ignorando may√∫sculas y espacios)
  const lines = robotsTxt.split(/\r?\n/);
  for (const line of lines) {
    const match = line.match(/^\s*Sitemap:\s*(.+)$/i);
    if (match) {
      return match[1].trim();
    }
  }
  return null; // No encontrado
}

const sitemapUrl = extractSitemapUrl(robotsTxtContent);

// Devolver JSON con la URL del sitemap (o null si no hay)
return [{ json: { sitemapUrl } }];

