// Obtenemos el array de URLs del JSON
const urlsArray = $input.first().json.urlset.url;

// Creamos un objeto donde cada clave es "url 1", "url 2", etc.
const result = {};
urlsArray.forEach((item, index) => {
  if (item.loc) {
    result[`url ${index + 1}`] = item.loc;
  }
});

// Devolvemos el objeto
return [
  {
    json: {
      urls: result
    }
  }
];
