// Validate the received JSON
const payload = $input.item.json.body;

// Check that required fields exist
if (!payload) {
  return {
    json: {
      success: false,
      message: "No payload received in the request"
    }
  };
}

// Ensure correct format for a workflow
const requiredFields = ['name', 'nodes'];
for (const field of requiredFields) {
  if (!payload[field]) {
    return {
      json: {
        success: false,
        message: `The field '${field}' is required in the workflow`
      }
    };
  }
}

// Ensure connections and settings exist
if (!payload.connections) payload.connections = {};
if (!payload.settings) payload.settings = {};

// Verify that there is at least one node
if (!Array.isArray(payload.nodes) || payload.nodes.length === 0) {
  return {
    json: {
      success: false,
      message: "The workflow must have at least one node"
    }
  };
}

// Validate each node's required fields
for (const node of payload.nodes) {
  const nodeRequiredFields = ['id', 'name', 'type', 'position'];
  for (const field of nodeRequiredFields) {
    if (!node[field]) {
      return {
        json: {
          success: false,
          message: `Node '${node.name || 'unknown'}' is missing required field '${field}'`
        }
      };
    }
  }

  // Ensure parameters exists
  if (!node.parameters) node.parameters = {};
  if (!node.typeVersion) node.typeVersion = 1;
}

// Prepare the workflow object for the API
const apiWorkflow = {
  name: payload.name,
  nodes: payload.nodes,
  connections: payload.connections,
  settings: payload.settings
};

// If everything is valid, proceed
return {
  json: {
    success: true,
    apiWorkflow: apiWorkflow
  }
};
