const updates = $json;

if (updates.message?.text) {
	return [
		{
			json: {
				type: "text",
				content: updates.message.text
			}
		}
	];
}

if (updates.message?.voice) {
	return [
		{
			json: {
				type: "voice",
				file_id: updates.message.voice.file_id
			}
		}
	];
}

return [];

const src = $json.output ?? $json;
const email = String(src.email || "contact@gmail.com").trim();
const subject = String(src.subject || "Compte rendu de r√©union").trim();
const body = String(src.body || "").trim();

// Nettoyer le HTML et le transformer en texte lisible
const clean = body
  .replace(/<br\s*\/?>/gi, "\n")
  .replace(/<\/p>/gi, "\n\n")
  .replace(/<\/?[^>]+(>|$)/g, "")
  .trim();

return [{
  Email: email,
  subject: subject,
  Report: clean
}];

const text = String($json.text || "").trim();
const lower = text.toLowerCase();

// 1) Direct email in message
const emailMatch = text.match(/[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i);
let email = emailMatch ? emailMatch[0].toLowerCase() : null;

// 2) Extract a domain hint from the message (fallback for building email)
let domainHint = null;
const domainRegex = /\b([a-z0-9][a-z0-9-]{0,63}\.)+[a-z]{2,}\b/i;
const domainMatch = text.match(domainRegex);
if (domainMatch) {
  const parts = domainMatch[0].toLowerCase().split('.').filter(Boolean);
  if (parts.length >= 2) {
    domainHint = `${parts[parts.length - 2]}.${parts[parts.length - 1]}`;
  }
}

// 3) If no direct email, try to build one from a "send to / envoyer √†" style mention
if (!email) {
  // Capture after signals like "envoyer √†", "envoie √†", "√† envoyer √†", "send to", "to"
  const toCue =
    /(√† envoyer √†|envoyer √†|envoie √†|envois √†|envoi √†|send to|to)\s+([^\n,;:]+)/i;
  const labelCue = /(mail|email|courriel|adresse)\s*[:\-]\s*([^\n,;:]+)/i;

  let target = null;
  const m1 = lower.match(toCue);
  const m2 = lower.match(labelCue);
  if (m1 && m1[2]) target = m1[2];
  else if (m2 && m2[2]) target = m2[2];

  if (target) {
    target = target
      .replace(/^[¬´"'\(\[]+|[¬ª"'\)\]]+$/g, "") // trim quotes/brackets
      .trim();

    // If target already contains an email, use it
    const tEmail = target.match(/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}/i);
    if (tEmail) {
      email = tEmail[0].toLowerCase();
    } else {
      // If target looks like "firstname.lastname" or "firstname lastname", normalize it
      // Remove common filler words
      target = target
        .replace(/\b(chez|√†|au|pour|de|du|la|le|l'|mr|mme|m\.)\b/g, " ")
        .replace(/\s+/g, " ")
        .trim();

      // Keep only letters, spaces, dots, hyphens, underscores
      target = target.replace(/[^a-z√†√¢√§√ß√©√®√™√´√Æ√Ø√¥√∂√π√ª√º√ø\s._-]/g, "");

      // Build local part
      const ascii = target
        .normalize("NFD")
        .replace(/\p{Diacritic}/gu, "")
        .toLowerCase();

      // Split by spaces, dots, underscores, hyphens
      const tokens = ascii.split(/[\s._-]+/).filter(Boolean);

      let local = null;
      if (tokens.length >= 2) {
        local = `${tokens[0]}.${tokens[tokens.length - 1]}`;
      } else if (tokens.length === 1) {
        local = tokens[0];
      }

      if (local) {
        // If target contains something like ".com" we treat it as a domain hint
        let domainFromTarget = null;
        const tDomain = target.match(domainRegex);
        if (tDomain) {
          const p = tDomain[0].split(".").filter(Boolean);
          if (p.length >= 2)
            domainFromTarget = `${p[p.length - 2]}.${p[p.length - 1]}`.toLowerCase();
        }

        const finalDomain = domainFromTarget || domainHint || "gmail.com";
        email = `${local}@${finalDomain}`.replace(/\.{2,}/g, ".");
      }
    }
  }
}

// 4) Final fallback
if (!email) {
  email = "contact@gmail.com";
}

// Preserve your previous outputs too
let foundDomain = null;
if (domainHint) foundDomain = domainHint;

return [{
  input: { text },
  email,
  domain: foundDomain,
  domainUrl: foundDomain ? `https://${foundDomain}` : null
}];
