// Get model output from previous node
const rawOutput =$input.first().json.output ;

// 1. Remove code fences if present
let cleaned = rawOutput.replace(/```json|```/g, "").trim();

// 2. Parse into JSON
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (e) {
  throw new Error("Failed to parse model output: " + e.message);
}

// 3. Extract query string
const queryString = parsed[0].query;

// 4. Convert query string into key-value pairs
const params = {};
queryString.split("&").forEach(pair => {
  const [key, value] = pair.split("=");
  params[key] = decodeURIComponent(value || "");
});

// Return usable fields for Gmail node
return [
  {
    json: {
      sendTo: params.sendTo,
      subject: params.subject,
      message: params.message,
      bccList: params.bccList,
      ccList: params.ccList,
      senderName: params.senderName,
      emailType: params.emailType
    }
  }
];
