// 4‚Äì6 digits; change 1000/9000 if you want 5 or 6 digits
function gen() { return (Math.floor(1000 + Math.random()*9000)).toString(); }

// If you want to reduce collisions per chat, you can later add a quick Airtable
// search for this chat_id and re-gen if the code exists. For most cases, this is fine.
return $input.all().map(i => ({
  json: { ...i.json, code: gen() }
}));

const text = ($json.message?.text ?? $json.callback_query?.data ?? '').trim();
const chatId = String($json.message?.chat?.id ?? $json.callback_query?.message?.chat?.id ?? '');

const m = text.match(/\b(\d{4,6})\b/);     // accept 4‚Äì6 digits anywhere: "5855", "YES 5855", "stop 5855"
const yesOnly = /^yes$/i.test(text);       // plain YES (no code)

return [{
  json: {
    chat_id: chatId,
    raw: text,
    code: m ? m[1] : '',
    has_code: !!m,
    yes_only: yesOnly
  }
}];
