// Ottieni il campo con la stringa originale (modifica il campo se necessario)
const inputString = $json["Reference images"];

// 1. Rimuove spazi extra e newline
// 2. Divide la stringa in un array
// 3. Filtra gli elementi vuoti
// 4. Ritorna come array JSON

const urlsArray = inputString
  .split(/,\s*|\r\n+/) // divide su virgole e newline
  .filter(url => url.trim() !== "");

return [
  {
    json: {
      reference_image_urls: urlsArray
    }
  }
];
