data = _input.first().json['data']

grouped_models = {}

for item in data:
    if item.get('type') != 'chat-completion':
        continue

    developer = item.get('info', {}).get('developer', 'Unknown')
    model_entry = {
        'id': item.get('id'),
        'name': item.get('info', {}).get('name', item.get('id'))
    }

    if developer not in grouped_models:
        grouped_models[developer] = []

    grouped_models[developer].append(model_entry)

