const text = $('üì© Receive Telegram Message').first().json.message.text;

if (!text.startsWith('#')) {
  throw new Error('Message must start with a #model_tag');
}

const parts = text.split(' ', 2);

if (parts.length &lt; 2) {
  throw new Error('Message must contain both #model_tag and prompt text.');
}

const model_id = parts[0].substring(1);  // –£–±–∏—Ä–∞–µ–º #
const message = parts[1];

return [
  {
    json: {
      model_id,
      message
    }
  }
];
