// === HELPER FUNCTIONS (email-safe) ===
function formatDuration(ms){ if(!ms||ms<=0) return ''; const m=Math.floor(ms/60000),h=Math.floor(m/60),r=m%60; return h>0?`${h}h ${r}m`:`${r}m`; }
function pill(text){ if(!text) return ''; return `<span style="background:#2a4a6a;color:#ffffff;border:1px solid #4a6a8a;border-radius:999px;padding:4px 10px;font-size:12px;line-height:1;display:inline-block;margin:0 6px 6px 0;">${String(text).toUpperCase()}</span>`; }
function sectionHeader(label, emoji){ return `
  <tr>
    <td align="center" style="padding:4px 24px 14px 24px;">
      <table role="presentation" cellpadding="0" cellspacing="0" border="0" style="background:#0f385b;border:1px solid #2a4a6a;border-radius:999px;">
        <tr>
          <td style="padding:10px 18px;">
            <span style="font:600 18px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#ffffff;">
              ${emoji} ${label}
            </span>
          </td>
        </tr>
      </table>
    </td>
  </tr>`; }

// === PLACEHOLDERS (replace these) ===
const tautulliUrl = 'https://YOUR_TAUTULLI_URL';
const plexApiToken = 'YOUR_PLEX_TOKEN';
const plexServerId  = 'YOUR_PLEX_SERVER_ID';

// === DATA (be tolerant to slightly different shapes) ===
const allRecentlyAdded = $input.all().flatMap(i => i.json?.response?.data?.recently_added || i.json?.data?.recently_added || []);
const movies  = allRecentlyAdded.filter(i => i.media_type === 'movie');
const tvItems = allRecentlyAdded.filter(i => ['episode','show','season'].includes(i.media_type));

// === RENDERERS ===
function movieRow(item){
  const title=item.title||'Untitled';
  const year=item.year?` (${item.year})`:'';
  const rating=item.audience_rating?`‚≠ê ${item.audience_rating}`:'';
  const dur=item.duration?`&nbsp;&nbsp;üïí ${formatDuration(item.duration)}`:'';
  const genres=Array.isArray(item.genres)?item.genres:[];
  const contentRating=item.content_rating?pill(item.content_rating):'';
  const summary=item.summary||'No description provided.';
  const directors=Array.isArray(item.directors)?item.directors:[];
  const actors=Array.isArray(item.actors)?item.actors:[];
  const plexLink=`https://app.plex.tv/desktop/#!/server/${plexServerId}/details?key=/library/metadata/${item.rating_key}`;
  const thumbUrl=item.thumb?`${tautulliUrl}/pms_image_proxy?img=${encodeURIComponent(item.thumb)}&X-Plex-Token=${plexApiToken}`:'https://placehold.co/120x180/333333/ffffff?text=NO+IMAGE';
  return `
  <tr>
    <td style="padding:0 24px 22px 24px;">
      <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="100%" style="background:#0f385b;border:1px solid #2a4a6a;border-radius:12px;">
        <tr>
          <td class="stack t-center" width="120" valign="top" style="padding:18px;">
            <a href="${plexLink}">
              <img class="img-resp" src="${thumbUrl}" alt="${title}" width="120" style="display:block;width:120px;height:auto;border-radius:10px;">
            </a>
          </td>
          <td class="stack" valign="top" style="padding:18px 18px 18px 0;">
            <h4 style="margin:0 0 8px 0;font:700 18px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;">
              <a href="${plexLink}" style="color:#ffffff;text-decoration:none;">${title}${year}</a>
            </h4>
            <p style="margin:0 0 10px 0;font:13px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#cccccc;">${rating}${dur}</p>
            <div style="margin:0 0 12px 0;">${contentRating}${genres.map(g=>pill(g)).join('')}</div>
            <p style="margin:0 0 12px 0;font:14px/1.6 -apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#dddddd;">${summary}</p>
            <p style="margin:0 0 14px 0;font:12px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#bbbbbb;border-top:1px solid #2a4a6a;padding-top:8px;">
              ${directors.length?`<strong style=\"color:#cccccc;\">Director:</strong> ${directors.join(', ')}<br>`:''}
              ${actors.length?`<strong style=\"color:#cccccc;\">Starring:</strong> ${actors.slice(0,3).join(', ')}`:''}
            </p>
            <table role="presentation" cellpadding="0" cellspacing="0" border="0"><tr><td bgcolor="#e5a00d" style="border-radius:10px;"><a href="${plexLink}" style="display:inline-block;padding:12px 18px;font:700 14px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#ffffff;text-decoration:none;border-radius:10px;">Watch on Plex</a></td></tr></table>
          </td>
        </tr>
      </table>
    </td>
  </tr>`;
}

function tvRow(item){
  let displayTitle=item.title||'Untitled', displaySubtitle='', thumb=item.thumb, key=item.rating_key;
  if(item.media_type==='episode'){
    displayTitle=item.grandparent_title||displayTitle;
    const s=item.parent_media_index?String(item.parent_media_index).padStart(2,'0'):'';
    const e=item.media_index?String(item.media_index).padStart(2,'0'):'';
    displaySubtitle=`S${s} E${e}${item.title?': '+item.title:''}`;
    thumb=item.grandparent_thumb||item.parent_thumb||item.thumb;
    key=item.grandparent_rating_key||key;
  } else if(item.media_type==='season'){
    displayTitle=item.parent_title||displayTitle;
    displaySubtitle=item.title||'';
    thumb=item.parent_thumb||item.thumb;
    key=item.parent_rating_key||key;
  }
  const year=item.year?` (${item.year})`:'';
  const rating=item.audience_rating?`‚≠ê ${item.audience_rating}`:'';
  const dur=(item.media_type==='episode'&&item.duration)?`&nbsp;&nbsp;üïí ${formatDuration(item.duration)}`:'';
  const genres=Array.isArray(item.genres)?item.genres:[];
  const contentRating=item.content_rating?pill(item.content_rating):'';
  const summary=item.summary||'No description provided.';
  const plexLink=`https://app.plex.tv/desktop/#!/server/${plexServerId}/details?key=/library/metadata/${key}`;
  const thumbUrl=thumb?`${tautulliUrl}/pms_image_proxy?img=${encodeURIComponent(thumb)}&X-Plex-Token=${plexApiToken}`:'https://placehold.co/120x180/333333/ffffff?text=NO+IMAGE';
  return `
  <tr>
    <td style="padding:0 24px 22px 24px;">
      <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="100%" style="background:#0f385b;border:1px solid #2a4a6a;border-radius:12px;">
        <tr>
          <td class="stack t-center" width="120" valign="top" style="padding:18px;">
            <a href="${plexLink}">
              <img class="img-resp" src="${thumbUrl}" alt="${displayTitle}" width="120" style="display:block;width:120px;height:auto;border-radius:10px;">
            </a>
          </td>
          <td class="stack" valign="top" style="padding:18px 18px 18px 0;">
            <h4 style="margin:0 0 6px 0;font:700 18px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;">
              <a href="${plexLink}" style="color:#ffffff;text-decoration:none;">${displayTitle}${year}</a>
            </h4>
            ${displaySubtitle?`<p style=\"margin:0 0 8px 0;font:13px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#cccccc;\">${displaySubtitle}</p>`:''}
            <p style="margin:0 0 10px 0;font:13px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#cccccc;">${rating}${dur}</p>
            <div style="margin:0 0 12px 0;">${contentRating}${genres.map(g=>pill(g)).join('')}</div>
            <p style="margin:0 0 12px 0;font:14px/1.6 -apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#dddddd;">${summary}</p>
            <table role="presentation" cellpadding="0" cellspacing="0" border="0"><tr><td bgcolor="#e5a00d" style="border-radius:10px;"><a href="${plexLink}" style="display:inline-block;padding:12px 18px;font:700 14px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#ffffff;text-decoration:none;border-radius:10px;">Watch on Plex</a></td></tr></table>
          </td>
        </tr>
      </table>
    </td>
  </tr>`;
}

// === LIGHT CSS ===
const css = `@media only screen and (max-width:600px){ .w-650{width:100% !important} .stack{display:block !important;width:100% !important} .img-resp{width:55% !important;height:auto !important;max-width:200px !important;margin:0 auto !important} .t-center{text-align:center !important} }`;

// === BUILD HTML ===
let html = `<!doctype html>
<html lang="en">
<head>
<meta http-equiv="x-ua-compatible" content="ie=edge">
<meta charset="utf-8">
<meta name="viewport" content="width=device-width,initial-scale=1">
<title>Plex Newsletter</title>
<style type="text/css">${css}</style>
</head>
<body style="margin:0;padding:0;background:#001f3f;">
  <center style="width:100%;background:#001f3f;">
    <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="100%">
      <tr>
        <td align="center" style="padding:20px 10px;">
          <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="650" class="w-650" style="width:650px;max-width:650px;">
            <tr><td style="padding:0;"><img src="https://placehold.co/1200x300/001f3f/ffffff?text=Plex+Newsletter" alt="Plex Weekly Newsletter" width="650" style="display:block;width:100%;height:auto;border-radius:12px;"></td></tr>
            <tr><td style="height:20px;font-size:1px;line-height:1px;">&nbsp;</td></tr>
            <tr><td align="center" style="padding:18px;border:1px solid #2a4a6a;background:#0d2c4a;border-radius:12px;"><p style="margin:0;font:16px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#ffffff;">Best Movies and Shows in one place!</p></td></tr>
            <tr><td style="height:12px;font-size:1px;line-height:1px;">&nbsp;</td></tr>
            <tr><td align="center"><p style="margin:0;font:13px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#a9a9a9;">Last Updated: ${new Date().toLocaleDateString('en-GB')}</p></td></tr>
            <tr><td style="height:26px;font-size:1px;line-height:1px;">&nbsp;</td></tr>
            <tr><td>
              <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="100%" style="background:#0d2c4a;border-radius:15px;border:1px solid #2a4a6a;">
                <tr><td style="padding:22px 24px 6px 24px;"><h2 style="margin:0 0 6px 0;text-align:center;font:700 22px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#ffffff;">üÜï New This Week</h2><p style="margin:0 0 8px 0;text-align:center;font:14px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#a9a9a9;">Freshly added Movies & TV from your Plex.</p></td></tr>
                ${movies.length?sectionHeader('New Movies','üéûÔ∏è'):''}
                ${movies.map(movieRow).join('')}
                ${tvItems.length?sectionHeader('New TV Shows','üì∫'):''}
                ${tvItems.map(tvRow).join('')}
                ${!movies.length && !tvItems.length ? `<tr><td style=\"padding:0 24px 24px 24px;\"><p style=\"margin:0;text-align:center;font:14px/-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif;color:#dddddd;\">No new items were added recently. Check back soon!</p></td></tr>`:``}
                <tr><td style="height:10px;font-size:1px;line-height:1px;">&nbsp;</td></tr>
              </table>
            </td></tr>
            <tr><td style="height:28px;font-size:1px;line-height:1px;">&nbsp;</td></tr>
          </table>
        </td>
      </tr>
    </table>
  </center>
</body>
</html>`;

return [{ json: { htmlBody: html } }];

const recipients = [
  'example1@example.com',
  'example2@example.com'
];

const htmlBody = $input.item.json.htmlBody;
const items = [];
for (const email of recipients) {
  items.push({ json: { htmlBody, recipientEmail: email } });
}
return items;
