const companies = [];

for (const item of $input.all()) {
  // Get company name from the correct field key: "List"
  const companyName = item.json.List;

  if (companyName && companyName.trim() !== '') {
    companies.push({
      company: companyName.trim(),
      row: item.json.row_number || companies.length + 2
    });
  }
}

// Return each company as a separate item
return companies.map(company => ({ json: company }));

const inputData = $input.all();
const results = [];

for (const item of inputData) {
  const searchResults = item.json;

  // === Helper Functions (same as before) ===

  function extractCompanyName(searchData) {
    if (searchData.search_parameters?.q) {
      const query = searchData.search_parameters.q.toLowerCase();
      const cleanQuery = query
        .replace(/competitors?/gi, '')
        .replace(/competition/gi, '')
        .replace(/vs/gi, '')
        .replace(/alternatives?/gi, '')
        .trim();
      const companyName = cleanQuery.split(' ')[0];
      return companyName.charAt(0).toUpperCase() + companyName.slice(1);
    }

    if (searchData.organic_results?.[0]?.title) {
      const title = searchData.organic_results[0].title;
      const match = title.match(/(\w+)\s+competitors?/i);
      if (match) return match[1];
    }

    return "Unknown Company";
  }

  function extractCompetitors(searchData) {
    const competitors = new Set();

    if (searchData.related_questions) {
      searchData.related_questions.forEach(q => {
        q.list?.forEach(item => {
          const clean = item.replace(/\.$/, '').trim();
          if (clean.length > 1) competitors.add(clean);
        });
      });
    }

    if (searchData.ai_overview?.text_blocks) {
      searchData.ai_overview.text_blocks.forEach(block => {
        block.list?.forEach(item => {
          const clean = item.title?.replace(/:$/, '').trim();
          if (clean?.length > 1) competitors.add(clean);
        });
      });
    }

    if (searchData.organic_results) {
      searchData.organic_results.forEach(result => {
        const snippet = result.snippet;
        if (snippet) {
          const patterns = [
            /competitors?[^.]*?include[^.]*?([A-Z][a-zA-Z\s,&.]+)/gi,
            /rivals?[^.]*?include[^.]*?([A-Z][a-zA-Z\s,&.]+)/gi,
            /competition[^.]*?from[^.]*?([A-Z][a-zA-Z\s,&.]+)/gi
          ];
          patterns.forEach(pattern => {
            const matches = snippet.match(pattern);
            if (matches) {
              matches.forEach(match => {
                match
                  .split(/,|\sand\s|&/)
                  .map(name => name.replace(/[^a-zA-Z\s]/g, '').trim())
                  .forEach(name => {
                    if (name.length > 2 && !name.toLowerCase().includes('competitor'))
                      competitors.add(name);
                  });
              });
            }
          });
        }
      });
    }

    if (searchData.answer_box?.expanded_list) {
      searchData.answer_box.expanded_list.forEach(item => {
        if (item.title) competitors.add(item.title);
      });
    }

    return Array.from(competitors).slice(0, 10);
  }

  function getTopSource(searchData) {
    return searchData.organic_results?.[0]?.link || null;
  }

  // === Main Logic ===
  try {
    const company = extractCompanyName(searchResults);
    const competitorsList = extractCompetitors(searchResults);
    const topSource = getTopSource(searchResults);

    results.push({
      json: {
        company,
        competitors: competitorsList.join(', ') || 'No competitors found',
        competitor_count: competitorsList.length,
        top_source: topSource,
        search_query: searchResults.search_parameters?.q || 'N/A',
        total_results: searchResults.search_information?.total_results || 0,
        extraction_timestamp: new Date().toISOString()
      }
    });
  } catch (err) {
    results.push({
      json: {
        error: `Extraction failed: ${err.message}`,
        company: "Unknown",
        competitors: "",
        competitor_count: 0,
        top_source: null
      }
    });
  }
}

return results;
