// Process crypto data and calculate movements
let processedStocks = [];

// Log the raw input for debugging
console.log('Raw API Input:', JSON.stringify($input.all(), null, 2));

// Aggregate data from all input items
$input.all().forEach(item => {
  const stockData = item.json;
  if (stockData && stockData.name && stockData.price_change_percentage_24h !== undefined) {
    const percentChange = parseFloat(stockData.price_change_percentage_24h);
    const price = parseFloat(stockData.current_price);
    const change = parseFloat(stockData.price_change_24h);

    processedStocks.push({
      name: stockData.name,
      symbol: stockData.symbol.toUpperCase(),
      price: isNaN(price) ? null : price,
      change: isNaN(change) ? null : change,
      percent_change: isNaN(percentChange) ? null : percentChange,
      volume: stockData.total_volume ? parseInt(stockData.total_volume) : null,
      high: stockData.high_24h ? parseFloat(stockData.high_24h) : null,
      low: stockData.low_24h ? parseFloat(stockData.low_24h) : null,
      movement_type: percentChange > 0 ? 'gain' : 'loss',
      abs_percent_change: Math.abs(percentChange)
    });
  }
});

// Log processed stocks for debugging
console.log('Processed Stocks:', JSON.stringify(processedStocks, null, 2));

// Sort by absolute percentage change (biggest movers)
processedStocks.sort((a, b) => b.abs_percent_change - a.abs_percent_change);

// Take top 100 biggest movers, gainers, and losers
const topMovers = processedStocks.slice(0, 100);
const gainers = processedStocks.filter(stock => stock.percent_change > 0).slice(0, 100);
const losers = processedStocks.filter(stock => stock.percent_change < 0).slice(0, 100);

// Set current date and time in IST (02:33 PM IST, August 22, 2025)
const currentDate = new Date('2025-08-22T14:33:00+05:30').toLocaleString('en-IN', {

  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

return [{
  json: {
    date: currentDate, // Reflects 02:33 PM IST, August 22, 2025
    total_stocks: processedStocks.length,
    top_gainers: gainers,
    top_losers: losers,
    biggest_movers: topMovers
  }
}];

