// Get the raw data from the HTTP Request
const data = $input.item.json;

// Get the timestamp for 24 hours ago
const twentyFourHoursAgo = Date.now() - (24 * 60 * 60 * 1000);

// Filter messages to only include those from the last 24 hours
// and format them into a clean transcript.
const transcript = data.result
  .filter(update => {
    // Ensure the message exists and has a date
    if (!update.message || !update.message.date) {
      return false;
    }
    // Telegram's date is in seconds, convert to milliseconds
    const messageDate = update.message.date * 1000;
    return messageDate > twentyFourHoursAgo && update.message.text;
  })
  .map(update => {
    const user = update.message.from.first_name || 'Unknown User';
    const text = update.message.text;
    return `${user}: ${text}`;
  })
  .join('\n');

// Return the final transcript. If it's empty, return a message.
if (transcript.length === 0) {
  return [{ json: { transcript: "No new messages in the last 24 hours." } }];
}

return [{ json: { transcript: transcript } }];

const escapeMarkdownV2 = (text) => {
  return text
    .replace(/_/g, '\\_')
    .replace(/\*/g, '\\*')
    .replace(/\[/g, '\\[')
    .replace(/\]/g, '\\]')
    .replace(/\(/g, '\\(')
    .replace(/\)/g, '\\)')
    .replace(/~/g, '\\~')
    .replace(/`/g, '\\`')
    .replace(/>/g, '\\>')
    .replace(/#/g, '\\#')
    .replace(/\+/g, '\\+')
    .replace(/-/g, '\\-')
    .replace(/=/g, '\\=')
    .replace(/\|/g, '\\|')
    .replace(/{/g, '\\{')
    .replace(/}/g, '\\}')
    .replace(/\./g, '\\.')
    .replace(/!/g, '\\!');
};
return {
  json: {
    text: escapeMarkdownV2($('Generate Summary with Gemini').first().json.content.parts[0].text)
  }
}
