parameters.jsonBody:
={
  "urls": [
    "https://www.quiverquant.com/congresstrading/*"
  ],
  "prompt": "Extract all notable congress member trades over $50,000 USD in the past 1 month. Include the Congress Member's name, party, the Stock/Asset purchased or sold, the Amount of the transaction, and the date of the transaction.",
  "schema": {
    "type": "object",
    "properties": {
      "notable_trades": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "congress_member_name": {
              "type": "string"
            },
            "party": {
              "type": "string"
            },
            "stock_or_asset": {
              "type": "string"
            },
            "amount": {
              "type": "number"
            },
            "transaction_date": {
              "type": "string"
            }
          },
          "required": [
            "congress_member_name",
            "stock_or_asset",
            "amount",
            "transaction_date"
          ]
        }
      }
    },
    "required": [
      "notable_trades"
    ]
  }
}
