const stockData = $input.first().json;
let processedStocks = [];

// Check for API error
if (stockData.error) {
  throw new Error(`Twelve Data API Error: ${stockData.error.message}`);
}

// Handle single or multiple stock responses
let quotes = [];
if (Array.isArray(stockData)) {
  quotes = stockData;
} else if (stockData.symbol) {
  quotes = [stockData];
} else {
  quotes = Object.values(stockData).filter(item => item.symbol);
}

for (let stock of quotes) {
  if (stock && stock.symbol && stock.percent_change) {
    const percentChange = parseFloat(stock.percent_change);
    const price = parseFloat(stock.close || stock.price);
    const change = parseFloat(stock.change);
    processedStocks.push({
      symbol: stock.symbol,
      name: stock.name || stock.symbol,
      price: isNaN(price) ? null : price,
      change: isNaN(change) ? null : change,
      percent_change: isNaN(percentChange) ? null : percentChange,
      volume: stock.volume ? parseInt(stock.volume) : null,
      movement_type: percentChange > 0 ? 'gain' : 'loss',
      abs_percent_change: Math.abs(percentChange)
    });
  }
}

// Sort by absolute percentage change
processedStocks.sort((a, b) => b.abs_percent_change - a.abs_percent_change);

// Top 5 movers
const topMovers = processedStocks.slice(0, 5);
const gainers = topMovers.filter(stock => stock.percent_change > 0).slice(0, 3);
const losers = topMovers.filter(stock => stock.percent_change < 0).slice(0, 3);

const currentDate = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

return [{ json: { date: currentDate, total_stocks: processedStocks.length, top_gainers: gainers, top_losers: losers, biggest_movers: topMovers } }];

