// URL Preprocessing - Fix domain formats and add protocols
return $input.all().map(item => {
  let domain = item.json.Domain || "";

  // Clean up domain
  domain = domain.trim();

  // Add https:// if no protocol exists
  if (domain && !domain.startsWith('http://') && !domain.startsWith('https://')) {
    domain = 'https://' + domain;
  }

  // Remove trailing slashes
  domain = domain.replace(/\/$/, '');

  return {
    ...item.json,
    Domain: domain,
    OriginalDomain: item.json.Domain
  };
});

//Enhanced Technology Detection with Proper Domain Extraction

const processItem = (item, index) => {
  try {
    console.log(`=== Processing Item ${index + 1} ===`);
    console.log('Item structure:', JSON.stringify(Object.keys(item), null, 2));
    console.log('Item.json keys:', item.json ? Object.keys(item.json) : 'No json property');

    // FIXED DOMAIN EXTRACTION - Multiple approaches for n8n compatibility
    let domain = "Unknown Domain";

    // Method 1: Direct domain field access (most common in n8n)
    if (item.json?.domain && typeof item.json.domain === 'string') {
      domain = item.json.domain.trim();
      console.log('Found domain in item.json.domain:', domain);
    }
    // Method 2: Capital D Domain field
    else if (item.json?.Domain && typeof item.json.Domain === 'string') {
      domain = item.json.Domain.trim();
      console.log('Found domain in item.json.Domain:', domain);
    }
    // Method 3: URL field that might contain domain
    else if (item.json?.url && typeof item.json.url === 'string') {
      domain = item.json.url.trim();
      console.log('Found domain in item.json.url:', domain);
    }
    // Method 4: Website field
    else if (item.json?.website && typeof item.json.website === 'string') {
      domain = item.json.website.trim();
      console.log('Found domain in item.json.website:', domain);
    }
    // Method 5: Site field
    else if (item.json?.site && typeof item.json.site === 'string') {
      domain = item.json.site.trim();
      console.log('Found domain in item.json.site:', domain);
    }
    // Method 6: Check if domain is passed alongside HTML data
    else if (item.json?.originalUrl && typeof item.json.originalUrl === 'string') {
      domain = item.json.originalUrl.trim();
      console.log('Found domain in item.json.originalUrl:', domain);
    }
    // Method 7: Extract from headers or metadata if available
    else if (item.json?.headers?.host) {
      domain = item.json.headers.host;
      console.log('Found domain in headers.host:', domain);
    }
    // Method 8: Look for any field that might contain domain info
    else {
      // Search through all fields for potential domain
      for (const [key, value] of Object.entries(item.json || {})) {
        if (typeof value === 'string' && 
            !key.toLowerCase().includes('html') && 
            !key.toLowerCase().includes('body') &&
            !key.toLowerCase().includes('content') &&
            !key.toLowerCase().includes('data') &&
            value.length < 200) {

          // Check if it looks like a domain
          if (value.includes('.') && 
              !value.includes('<!DOCTYPE') && 
              !value.includes('<html') &&
              (value.includes('http') || value.match(/^[a-zA-Z0-9][a-zA-Z0-9-]+\.[a-zA-Z]{2,}/))) {
            domain = value.trim();
            console.log(`Found potential domain in item.json.${key}:`, domain);
            break;
          }
        }
      }
    }

    // Clean domain name (remove protocols, www, paths)
    if (domain && domain !== "Unknown Domain") {
      const originalDomain = domain;
      domain = domain
        .replace(/^https?:\/\//, '')  // Remove protocol
        .replace(/^www\./, '')         // Remove www
        .replace(/\/$/, '')            // Remove trailing slash
        .split('/')[0]                 // Remove paths
        .split('?')[0]                 // Remove query params
        .split('#')[0];                // Remove fragments

      console.log(`Cleaned domain: ${originalDomain} -> ${domain}`);
    }

    // If still no domain found, try to extract from HTML meta tags
    if (domain === "Unknown Domain") {
      const html = item.json?.html || item.json?.data || item.json?.body || "";
      const ogUrlMatch = html.match(/<meta\s+property=["']og:url["']\s+content=["']([^"']+)["']/i);
      const canonicalMatch = html.match(/<link\s+rel=["']canonical["']\s+href=["']([^"']+)["']/i);

      if (ogUrlMatch && ogUrlMatch[1]) {
        domain = ogUrlMatch[1]
          .replace(/^https?:\/\//, '')
          .replace(/^www\./, '')
          .split('/')[0];
        console.log('Extracted domain from og:url meta tag:', domain);
      } else if (canonicalMatch && canonicalMatch[1]) {
        domain = canonicalMatch[1]
          .replace(/^https?:\/\//, '')
          .replace(/^www\./, '')
          .split('/')[0];
        console.log('Extracted domain from canonical link:', domain);
      }
    }

    console.log(`Final domain: ${domain}`);

    // Get HTML content for analysis
    const raw = item.json?.html || item.json?.data || item.json?.body || item.json?.content || "";
    const html = String(raw).toLowerCase();
    const originalHtml = String(raw);

    console.log(`HTML content length: ${raw.length}`);

    // Enhanced error checking with specific error types
    if (item.json?.error || !raw || raw.length < 100) {
      let specificError = "Site unreachable";
      let errorDetails = "";

      if (item.json?.error) {
        try {
          const errorStr = typeof item.json.error === 'object' ? 
            JSON.stringify(item.json.error) : String(item.json.error);
          errorDetails = errorStr.substring(0, 100);

          if (errorStr.includes('ENOTFOUND')) {
            specificError = "Domain not found (DNS error)";
          } else if (errorStr.includes('ECONNREFUSED')) {
            specificError = "Connection refused";
          } else if (errorStr.includes('ETIMEDOUT') || errorStr.includes('timeout')) {
            specificError = "Request timeout";
          } else if (errorStr.includes('certificate') || errorStr.includes('CERT')) {
            specificError = "SSL certificate error";
          } else if (errorStr.includes('ECONNRESET')) {
            specificError = "Connection reset";
          } else if (errorStr.includes('404')) {
            specificError = "Page not found (404)";
          } else if (errorStr.includes('403')) {
            specificError = "Access forbidden (403)";
          } else if (errorStr.includes('500')) {
            specificError = "Server error (500)";
          }
        } catch (e) {
          errorDetails = "Error parsing failed";
        }
      } else if (item.json?.statusCode && item.json.statusCode >= 400) {
        specificError = `HTTP ${item.json.statusCode} error`;
      } else if (!raw) {
        specificError = "No content received";
      } else if (raw.length < 100) {
        specificError = "Minimal content received";
      }

      return {
        Domain: domain,
        Platform: "Error - " + specificError,
        Catalog: "N/A",
        Cart: "N/A",
        Checkout: "N/A",
        Wishlist: "N/A",
        PWA: "N/A",
        Payment_Gateway: "N/A",
        Framework: "N/A",
        Confidence_Score: "0%",
        Last_Checked: new Date().toISOString().split('T')[0],
        Remarks: `Error: ${specificError}${errorDetails ? ' - ' + errorDetails : ''}`
      };
    }

    // Initialize detection arrays
    const platforms = [];
    const frameworks = [];
    const paymentGateways = [];
    let cart = "No", catalog = "No", checkout = "No", wishlist = "No", pwa = "No";

    // Safe pattern matching function
    const has = (patterns) => {
      return patterns.some(pattern => {
        try {
          if (pattern instanceof RegExp) {
            return pattern.test(html) || pattern.test(originalHtml);
          }
          return html.includes(String(pattern).toLowerCase());
        } catch (e) {
          console.log(`Pattern matching error: ${e.message}`);
          return false;
        }
      });
    };

    // ========== ENHANCED PLATFORM DETECTION ==========

    // Magento (Enhanced detection)
    if (has([
      /x-magento-init/i,
      /mage\/bootstrap/i,
      /static\/version\d+\/frontend/i,
      /var\s+BASE_URL\s*=.*mage/i,
      /Mage\.Cookies/i,
      "form_key",
      /magento\s?enterprise/i,
      "requirejs/require.js",
      "/static/frontend/",
      "magento"
    ])) platforms.push("Magento");

    // Shopify (Enhanced detection)
    if (has([
      /cdn\.shopify\.com/i,
      /assets\.shopify\.com/i,
      /checkout\.shopify\.com/i,
      /Shopify\.theme/i,
      /window\.ShopifyAnalytics/i,
      "x-shopify",
      "/cart.js",
      "/products.json",
      /generator["'][^>]*shopify/i
    ])) platforms.push("Shopify");

    // WooCommerce (Enhanced detection)
    if (has([
      /wp-content\/plugins\/woocommerce/i,
      /woocommerce_params/i,
      /wc-cart-fragments/i,
      /class="[^"]*woocommerce/i,
      /woocommerce-variation/i,
      "woocommerce",
      "woocommerce-cart"
    ])) platforms.push("WooCommerce");

    // BigCommerce
    if (has([
      "cdn.bcapp",
      "bigcommerce",
      "stencil-utils",
      "cornerstone",
      /bigcommerce/i
    ])) platforms.push("BigCommerce");

    // Custom E-commerce Detection (for sites like HSP Diesel)
    if (has([
      /add\s+to\s+cart/i,
      /shopping\s+cart/i,
      /product\s+details/i,
      /buy\s+now/i,
      /add\s+to\s+bag/i,
      /purchase/i,
      /checkout/i,
      /price/i,
      /\$[0-9,]+/,
      /product\s+categories/i,
      /featured\s+products/i,
      /new\s+products/i,
      /online\s+store/i,
      /e-?commerce/i
    ])) {
      if (platforms.length === 0) {
        platforms.push("Custom E-commerce");
      }
    }

    // Other major platforms
    if (has(["static.squarespace.com", "sqs-layout", /generator[^>]*squarespace/i])) platforms.push("Squarespace");
    if (has(["wixstatic.com", "wix-code", "wixapps.net", /generator[^>]*wix/i])) platforms.push("Wix");
    if (has(["webflow.js", "data-wf-site", 'class="w-nav', /generator[^>]*webflow/i])) platforms.push("Webflow");
    if (has(["prestashop", "/modules/", "var prestashop", 'class="ps-'])) platforms.push("PrestaShop");
    if (has(["index.php?route=", "catalog/view/theme/", "opencart"])) platforms.push("OpenCart");

    // Enterprise platforms
    if (has(["demandware", "dwanalytics", "dwac", "checkout-begin", "sites-", /salesforce.*commerce/i])) platforms.push("Salesforce Commerce Cloud");
    if (has(["suitecommerce", ".ssp", "touchpoints", "scis", /netsuite/i])) platforms.push("NetSuite SuiteCommerce");
    if (has([/sap[.\s]hybris/i, /oracle[.\s]commerce/i, /ibm[.\s]websphere/i])) platforms.push("Enterprise Commerce");

    // Additional smaller platforms
    if (has(["oscommerce"])) platforms.push("osCommerce");
    if (has(["/mm5/", "miva merchant", "screen=plst"])) platforms.push("Miva Merchant");
    if (has(["volusion", "/v/vspfiles/", "checkout.asp"])) platforms.push("Volusion");
    if (has(["ecwid.min.js", "app.ecwid.com", "data-ecwid"])) platforms.push("Ecwid");
    if (has(["nopcommerce", /generator[^>]*nopcommerce/i])) platforms.push("nopCommerce");

    // ========== FRAMEWORK DETECTION ==========
    if (has(["next.js", "_next/", /Next\.js/i])) frameworks.push("Next.js");
    if (has(["nuxt.js", "_nuxt/", /Nuxt\.js/i])) frameworks.push("Nuxt.js");
    if (has([/react/i, "react.js", "react.min.js"])) frameworks.push("React");
    if (has([/vue\.js/i, "vue.min.js"])) frameworks.push("Vue.js");
    if (has([/angular/i, "angular.js"])) frameworks.push("Angular");
    if (has(["gatsby", /gatsby/i])) frameworks.push("Gatsby");
    if (has(["wordpress", "wp-content", "wp-includes"])) frameworks.push("WordPress");

    // ========== PAYMENT GATEWAY DETECTION ==========
    if (has(["stripe.js", /stripe/i, "js.stripe.com"])) paymentGateways.push("Stripe");
    if (has(["paypal.js", /paypal/i, "paypal.com"])) paymentGateways.push("PayPal");
    if (has(["square.js", /square/i])) paymentGateways.push("Square");
    if (has(["klarna", /klarna/i])) paymentGateways.push("Klarna");
    if (has(["razorpay", /razorpay/i])) paymentGateways.push("Razorpay");
    if (has(["braintree", /braintree/i])) paymentGateways.push("Braintree");

    // ========== ENHANCED FEATURE DETECTION ==========

    // Cart Detection (Enhanced)
    if (has([
      /add[\s\-_]?to[\s\-_]?(cart|bag|basket)/i,
      /shopping[\s\-_]?cart/i,
      /mini[\s\-_]?cart/i,
      /cart[\s\-_]?total/i,
      /quantity[\s\-_]?selector/i,
      /buy[\s\-]?now/i,
      /order[\s\-]?now/i,
      /add[\s\-]?to[\s\-]?bag/i,
      /purchase/i,
      /cart\.js/i,
      /shopping.*bag/i
    ])) cart = "Yes";

    // Catalog Detection (Enhanced)
    if (has([
      /catalog(ue)?/i,
      /product[\s\-_]?catalog/i,
      /category/i,
      /categories/i,
      /browse[\s\-_]?products/i,
      /product[\s\-_]?filter/i,
      /search[\s\-_]?results/i,
      /featured[\s\-_]?products/i,
      /new[\s\-_]?products/i,
      /top[\s\-_]?products/i,
      /popular[\s\-_]?products/i,
      /product[\s\-_]?bundles/i
    ])) catalog = "Yes";

    // Checkout Detection
    if (has([
      /checkout/i,
      /payment/i,
      /billing[\s\-_]?address/i,
      /shipping[\s\-_]?address/i,
      /place[\s\-_]?order/i,
      /complete[\s\-_]?purchase/i,
      /secure[\s\-_]?checkout/i,
      /proceed[\s\-_]?to[\s\-_]?checkout/i
    ])) checkout = "Yes";

    // Wishlist Detection
    if (has([
      /wish[\s\-_]?list/i,
      /save[\s\-_]?for[\s\-_]?later/i,
      /favorite/i,
      /add[\s\-_]?to[\s\-_]?wishlist/i
    ])) wishlist = "Yes";

    // PWA Detection
    if (has([
      "service-worker",
      "manifest.json",
      "workbox",
      /pwa/i,
      "sw.js"
    ])) pwa = "Yes";

    // ========== CONFIDENCE SCORING ==========
    let confidenceScore = 0;
    if (platforms.length > 0) confidenceScore += 40;
    if (cart === "Yes") confidenceScore += 20;
    if (checkout === "Yes") confidenceScore += 25;
    if (catalog === "Yes") confidenceScore += 10;
    if (paymentGateways.length > 0) confidenceScore += 5;

    confidenceScore = Math.min(confidenceScore, 100);

    // ========== REMARKS GENERATION ==========
    let remarks = "Basic Website";
    if (platforms.length || cart === "Yes" || catalog === "Yes" || checkout === "Yes") {
      remarks = "E-commerce Features Detected";
    } else if (frameworks.length) {
      remarks = "Website Framework Detected";
    } else if (html.length > 1000) {
      remarks = "Website Active - No E-commerce Detected";
    }

    console.log(`SUCCESS: ${domain} - ${platforms.join(', ')} - ${confidenceScore}%`);

    // Final output
    return {
      Domain: domain,
      Platform: platforms.length ? platforms.slice(0, 2).join("; ") : "Not Detected",
      Catalog: catalog,
      Cart: cart,
      Checkout: checkout,
      Wishlist: wishlist,
      PWA: pwa,
      Payment_Gateway: paymentGateways.length ? paymentGateways.slice(0, 2).join("; ") : "Not Detected",
      Framework: frameworks.length ? frameworks.slice(0, 2).join("; ") : "Not Detected",
      Confidence_Score: `${confidenceScore}%`,
      Last_Checked: new Date().toISOString().split('T')[0],
      Remarks: remarks
    };

  } catch (error) {
    console.log(`CRITICAL ERROR processing item ${index}:`, error.message);
    console.log('Error stack:', error.stack);

    // Try to at least get the domain even in error cases
    let errorDomain = "Processing Error";
    try {
      if (item.json?.domain) errorDomain = item.json.domain;
      else if (item.json?.Domain) errorDomain = item.json.Domain;
      else if (item.json?.url) errorDomain = item.json.url;
      else if (item.json?.website) errorDomain = item.json.website;
    } catch (e) {
      // Keep default error domain
    }

    return {
      Domain: errorDomain,
      Platform: "Processing Error",
      Catalog: "N/A",
      Cart: "N/A",
      Checkout: "N/A",
      Wishlist: "N/A",
      PWA: "N/A",
      Payment_Gateway: "N/A",
      Framework: "N/A",
      Confidence_Score: "0%",
      Last_Checked: new Date().toISOString().split('T')[0],
      Remarks: `Critical processing error: ${error.message}`
    };
  }
};

// Process all items with error tracking
try {
  const results = $input.all().map(processItem);
  console.log(`Successfully processed ${results.length} domains`);

  // Log summary of domains processed
  console.log('\n=== PROCESSING SUMMARY ===');
  results.forEach((result, idx) => {
    console.log(`${idx + 1}. ${result.Domain} - Platform: ${result.Platform} - Score: ${result.Confidence_Score}`);
  });

  return results;
} catch (globalError) {
  console.log('GLOBAL ERROR:', globalError.message);
  // Return minimal error response
  return [{
    Domain: "Global Error",
    Platform: "Processing Error",
    Catalog: "N/A",
    Cart: "N/A",
    Checkout: "N/A",
    Wishlist: "N/A",
    PWA: "N/A",
    Payment_Gateway: "N/A",
    Framework: "N/A",
    Confidence_Score: "0%",
    Last_Checked: new Date().toISOString().split('T')[0],
    Remarks: `Global processing error: ${globalError.message}`
  }];
}
