// Assume input is a single string like SRT format
const input = $input.first().json.subtitles[0].srt; // or wherever your caption string is

// Step-by-step cleanup
const cleaned = input
  // Remove caption numbers (lines with only digits)
  .replace(/^\d+\s*$/gm, "")
  // Remove timing lines
  .replace(/^\d{2}:\d{2}:\d{1,2},\d{1,3} --> \d{2}:\d{2}:\d{1,2},\d{1,3}\s*$/gm, "")
  // Remove leftover newlines
  .replace(/\n{2,}/g, "\n")
  // Trim and flatten everything to single paragraph
  .replace(/\n/g, " ")
  .trim();

return [{ cleanedText: cleaned }];
