const { answer, groundTruth = [] } = $input.item.json;

const scores = await Promise.all(groundTruth.map(truth =>
  cosineSimilarity(answer, truth.data)
));

const averageScore = scores.reduce((acc,i) => acc + i, 0) / scores.length;

return { json: { score: averageScore } };

function cosineSimilarity(a, b) {  
  let dotProduct = normA = normB = 0;
  for (let i = 0; i < a.length; i++) {
    dotProduct += a[i] * b[i];
    normA += a[i] ** 2;
    normB += b[i] ** 2;
  }
  return dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
}
