const limitPages = Math.ceil($('Set APIFY Token').first().json.limit / 100)
const totalComments = $('Run Apify Comments Scraper').last().json.summary.totalComments 
const availablePages = Math.ceil(totalComments / 100)

const totalRuns = availablePages <= limitPages ? availablePages : limitPages

let currentPage = 2


return {
  json: {
    items:
      Array.from({ length: totalRuns }, (_, i) => {
        return { currentPage: currentPage + i }
      })
  }
};

// Get all input items
