// Use reduce to filter unique profile_url values
const uniqueProfiles = items.reduce((acc, item) => {
  // Check if this profile_url is already in the accumulator
  console.log(item)
  if (item.author && !acc.some(i => i.profile_url === item.author.profile_url)) {
    acc.push(item.author);
  }
  return acc;
}, []);

return uniqueProfiles

const uniqueProfiles = $input.all().map(item => item.json.profile_url)

// Split the uniqueProfiles in batches of 500 for the Apify
const chunkSize = 500;
const batches = [];

for (let i = 0; i < uniqueProfiles.length; i += chunkSize) {
  batches.push(uniqueProfiles.slice(i, i + chunkSize));
}


return { batches };

function flatten(obj, prefix = '', res = {}) {
  for (const key in obj) {
    const value = obj[key];
    const prefixedKey = prefix ? `${prefix}_${key}` : key;
    if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
      flatten(value, prefixedKey, res);
    } else {
      res[prefixedKey] = value;
    }
  }
  return res;
}

return $input.first().json.items.map(item => ({
  json: flatten(item)
}));
