// Filter out already processed files
const files = $node['List New Invoices'].json.files;
const processedIds = $node['Check Already Processed'].json.map(row => row.file_id);

const newFiles = files.filter(file => !processedIds.includes(file.id));

return newFiles.map(file => ({ json: file }));

// Comprehensive invoice validation
const invoice = $node['Extract Invoice Data'].json.data;
const vendor = $node['Lookup Vendor'].json[0] || $node['Create New Vendor'].json[0];
let validationResult = {
  invoice: invoice,
  vendorId: vendor.vendor_id,
  vendorStatus: vendor.status,
  errors: [],
  warnings: [],
  requiresApproval: false,
  approvalLevel: 0
};

// Validate calculations
if (invoice.lineItems && invoice.lineItems.length > 0) {
  const calculatedSubtotal = invoice.lineItems.reduce((sum, item) => sum + (item.amount || 0), 0);
  if (Math.abs(calculatedSubtotal - invoice.subtotal) > 0.01) {
    validationResult.errors.push(`Line items total (${calculatedSubtotal}) doesn't match subtotal (${invoice.subtotal})`);
  }
}

// Validate tax calculations
const totalTax = invoice.taxDetails ? invoice.taxDetails.reduce((sum, tax) => sum + tax.amount, 0) : 0;
const calculatedTotal = (invoice.subtotal || 0) + totalTax;
if (Math.abs(calculatedTotal - invoice.total) > 0.01) {
  validationResult.errors.push(`Calculated total (${calculatedTotal}) doesn't match invoice total (${invoice.total})`);
}

// Check duplicate invoice
const duplicateCheck = await $node['Check Duplicate'].json;
if (duplicateCheck.length > 0) {
  validationResult.errors.push('Duplicate invoice detected');
}

// Determine approval requirements
if (invoice.total > 10000) {
  validationResult.requiresApproval = true;
  validationResult.approvalLevel = 3; // CFO
} else if (invoice.total > 5000) {
  validationResult.requiresApproval = true;
  validationResult.approvalLevel = 2; // Department Head
} else if (invoice.total > 1000 || vendor.status === 'pending_review') {
  validationResult.requiresApproval = true;
  validationResult.approvalLevel = 1; // Manager
}

// Check PO if provided
if (invoice.poNumber) {
  const poCheck = await $node['Check PO'].json;
  if (poCheck.length === 0) {
    validationResult.warnings.push('PO number not found in system');
  } else {
    const po = poCheck[0];
    if (invoice.total > po.remaining_amount) {
      validationResult.errors.push('Invoice amount exceeds PO remaining balance');
    }
  }
}

validationResult.isValid = validationResult.errors.length === 0;

return [{ json: validationResult }];

