// Generate secure approval link
const baseUrl = 'https://your-domain.com/approve';
const token = require('crypto').randomBytes(32).toString('hex');
const approvalData = {
  invoiceId: $json.invoice.invoiceNumber,
  vendorId: $json.vendorId,
  amount: $json.invoice.total,
  token: token,
  expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString()
};

// Store approval token in DB (not shown)
const approvalUrl = `${baseUrl}?token=${token}`;

return [{ json: { ...approvalData, approvalUrl } }];
