// Process and validate citations
const citations = $input.first().json.data;
const citationAnalysis = {
  summary: {
    totalCitations: 0,
    caseLaw: citations.caseCitations?.length || 0,
    statutes: citations.statuteCitations?.length || 0,
    regulations: citations.regulatoryCitations?.length || 0,
    academic: citations.academicCitations?.length || 0,
    other: citations.otherCitations?.length || 0
  },
  validation: {
    invalidCitations: [],
    warnings: []
  },
  academicDOIs: [],
  citationNetwork: {}
};

// Calculate total
citationAnalysis.summary.totalCitations = 
  citationAnalysis.summary.caseLaw + 
  citationAnalysis.summary.statutes + 
  citationAnalysis.summary.regulations + 
  citationAnalysis.summary.academic + 
  citationAnalysis.summary.other;

// Validate case citations
if (citations.caseCitations) {
  citations.caseCitations.forEach((cite, index) => {
    // Check for required fields
    if (!cite.reporter || !cite.volume || !cite.page) {
      citationAnalysis.validation.invalidCitations.push({
        type: 'case',
        index,
        citation: cite.caseName,
        issue: 'Missing reporter, volume, or page'
      });
    }

    // Build citation network (track which cases cite which)
    if (!citationAnalysis.citationNetwork[cite.caseName]) {
      citationAnalysis.citationNetwork[cite.caseName] = {
        citedIn: [citations.documentInfo.title],
        pageNumbers: [cite.pageNumber]
      };
    }
  });
}

// Validate statute citations
if (citations.statuteCitations) {
  citations.statuteCitations.forEach((cite, index) => {
    if (!cite.title || !cite.section) {
      citationAnalysis.validation.invalidCitations.push({
        type: 'statute',
        index,
        citation: `${cite.title} ${cite.code}`,
        issue: 'Missing title or section'
      });
    }
  });
}

// Extract DOIs for academic fetching
if (citations.academicCitations) {
  citations.academicCitations.forEach(cite => {
    if (cite.doi) {
      citationAnalysis.academicDOIs.push(cite.doi);
    } else {
      // Try to construct search query for papers without DOI
      citationAnalysis.validation.warnings.push({
        type: 'academic',
        citation: cite.title,
        warning: 'No DOI found - manual search may be needed'
      });
    }
  });
}

// Analyze citation patterns
const citationPatterns = {
  mostCitedCases: [],
  primaryAuthorities: [],
  secondaryAuthorities: []
};

// Identify primary authorities (statutes and binding cases)
citationPatterns.primaryAuthorities = [
  ...citations.statuteCitations?.map(c => `${c.title} ${c.code} § ${c.section}`) || [],
  ...citations.caseCitations?.filter(c => c.court?.includes('Supreme'))?.map(c => c.caseName) || []
];

// Identify secondary authorities
citationPatterns.secondaryAuthorities = 
  citations.academicCitations?.map(c => `${c.authors}, ${c.title}`) || [];

return [{
  json: {
    originalData: citations,
    analysis: citationAnalysis,
    patterns: citationPatterns,
    doisToFetch: citationAnalysis.academicDOIs.join(','),
    processedAt: new Date().toISOString()
  }
}];

// Generate comprehensive citation report
const citationData = $node['Has Academic DOIs?'].json;
const academicPapers = $json.publications || [];

// Create citation report
let report = `# Legal Citation Analysis Report\n\n`;
report += `**Document:** ${citationData.originalData.documentInfo.title}\n`;
report += `**Type:** ${citationData.originalData.documentInfo.documentType}\n`;
report += `**Date:** ${citationData.originalData.documentInfo.date}\n\n`;

report += `## Citation Summary\n\n`;
report += `- **Total Citations:** ${citationData.analysis.summary.totalCitations}\n`;
report += `- **Case Law:** ${citationData.analysis.summary.caseLaw}\n`;
report += `- **Statutes:** ${citationData.analysis.summary.statutes}\n`;
report += `- **Regulations:** ${citationData.analysis.summary.regulations}\n`;
report += `- **Academic:** ${citationData.analysis.summary.academic}\n`;
report += `- **Other:** ${citationData.analysis.summary.other}\n\n`;

// Add validation issues
if (citationData.analysis.validation.invalidCitations.length > 0) {
  report += `## Citation Issues\n\n`;
  citationData.analysis.validation.invalidCitations.forEach(issue => {
    report += `- **${issue.type}:** ${issue.citation} - ${issue.issue}\n`;
  });
  report += `\n`;
}

// Add case law section
if (citationData.originalData.caseCitations?.length > 0) {
  report += `## Case Law Citations\n\n`;
  citationData.originalData.caseCitations.forEach(cite => {
    report += `### ${cite.caseName}\n`;
    report += `- **Citation:** ${cite.volume} ${cite.reporter} ${cite.page} (${cite.year})\n`;
    report += `- **Court:** ${cite.court || 'Not specified'}\n`;
    report += `- **Context:** ${cite.context}\n`;
    report += `- **Page:** ${cite.pageNumber}\n\n`;
  });
}

// Add statute section
if (citationData.originalData.statuteCitations?.length > 0) {
  report += `## Statutory Citations\n\n`;
  citationData.originalData.statuteCitations.forEach(cite => {
    report += `- **${cite.title} ${cite.code} § ${cite.section}**${cite.subsection ? ` (${cite.subsection})` : ''}\n`;
    report += `  - Context: ${cite.context}\n`;
    report += `  - Page: ${cite.pageNumber}\n\n`;
  });
}

// Add academic section with fetched data
if (citationData.originalData.academicCitations?.length > 0) {
  report += `## Academic Citations\n\n`;
  citationData.originalData.academicCitations.forEach(cite => {
    report += `### ${cite.title}\n`;
    report += `- **Authors:** ${cite.authors}\n`;
    report += `- **Journal:** ${cite.journal}, Vol. ${cite.volume}, p. ${cite.page} (${cite.year})\n`;

    // Add fetched paper data if available
    const fetchedPaper = academicPapers.find(p => p.doi === cite.doi);
    if (fetchedPaper) {
      report += `- **Citations:** ${fetchedPaper.totalCitations || 0}\n`;
      report += `- **Abstract Available:** Yes\n`;
      if (fetchedPaper.pdfURL) {
        report += `- **Full Text:** [Available](${fetchedPaper.pdfURL})\n`;
      }
    }

    report += `- **Context:** ${cite.context}\n`;
    report += `- **Page:** ${cite.pageNumber}\n\n`;
  });
}

// Add citation patterns
report += `## Citation Analysis\n\n`;
report += `### Primary Authorities\n`;
citationData.patterns.primaryAuthorities.forEach(auth => {
  report += `- ${auth}\n`;
});
report += `\n### Secondary Authorities\n`;
citationData.patterns.secondaryAuthorities.forEach(auth => {
  report += `- ${auth}\n`;
});

return [{
  json: {
    report,
    citationData,
    academicPapers,
    exportFormat: 'markdown',
    generatedAt: new Date().toISOString()
  }
}];
