parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "name": { 
      "type": "string", 
      "description": "Product name/title" 
    },
    "description": { 
      "type": "string", 
      "description": "Product description or key features" 
    },
    "rating": { 
      "type": ["number", "null"], 
      "description": "Average rating (e.g., 4.5)" 
    },
    "reviews": { 
      "type": ["integer", "null"], 
      "description": "Number of reviews" 
    },
    "price": { 
      "type": ["string", "null"], 
      "description": "Product price with currency" 
    }
  },
  "required": ["name"]
}
