{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "InvoiceExtraction",
  "type": "object",
  "properties": {
    "modification_date": {
      "type": "string",
      "description": "Today's date in strictly DD/MM/YYYY format (e.g., 06/05/2025)."
    },
    "vendor": {
      "type": "string",
      "description": "Name of the company sending the invoice."
    },
    "items": {
      "type": "array",
      "description": "Line items included in the invoice.",
      "items": {
        "type": "object",
        "properties": {
          "description": {
            "type": "string",
            "description": "Description of the product or service."
          },
          "sub_total": {
            "type": "number",
            "description": "Total for this line before tax."
          },
          "iva_value": {
            "type": "number",
            "description": "Value of the VAT (IVA). If not present, return 0."
          },
          "total_amount": {
            "type": "number",
            "description": "Final total amount (subtotal + tax)."
          },
          "category": {
            "type": "string",
            "enum": [
              "Administrative expenses",
              "HR expenses",
              "Legal expenses",
              "Tax expenses",
              "Project expenses"
            ],
            "description": "Category of the cost."
          },
          "sub_category": {
            "type": "string",
            "enum": [
              "Contador",
              "Transportations, Flights",
              "Marketing",
              "Afiliación Artefact",
              "Equipos empleados",
              "Plataforma Reclutamiento",
              "Seguros empresa",
              "Other (documents, telco...)",
              "Payback Benjamin Solins",
              "Payback Ana Maria Cardenas",
              "Payback Felipe Garcia",
              "Salario Ordinario",
              "MiPlanilla",
              "Celular",
              "Prima",
              "Cowork",
              "GTG",
              "Teambuilding (lunch, coffee...)",
              "People Pass",
              "Bank Fees (subscription)",
              "Bank Fees (pago otros bancos)",
              "Bank Fees (nominas)",
              "Bank Fees (proveedores)",
              "Lawyers",
              "SST",
              "Fees",
              "Sanofi",
              "Totto",
              "BCP",
              "Retención ingresos Artefact",
              "Retefuente",
              "ReteICA",
              "IVA Ventas",
              "El 4/1000"
            ],
            "description": "Sub-category of the cost."
          }
        },
        "required": [
          "description",
          "sub_total",
          "iva_value",
          "total_amount",
          "category",
          "sub_category"
        ]
      }
    },
    "retefuente": {
      "type": "number",
      "description": "Withholding tax value. If not present, return 0."
    },
    "reteica": {
      "type": "number",
      "description": "Industry and commerce withholding tax value. If not present, return 0."
    },
    "number_of_items": {
      "type": "integer",
      "description": "How many items were generated."
    }
  },
  "required": [
    "modification_date",
    "vendor",
    "items",
    "retefuente",
    "reteica",
    "number_of_items"
  ]
}

