// Get values from input expressions
const serviceName = $('Form Submission Trigger').first().json.Service;
const state = $('Form Submission Trigger').first().json.State;
const country = $('Form Submission Trigger').first().json.Country;
const citiesString = $('Generate City List').first().json.output;

// Validate required fields
if (!serviceName || !state || !country || !citiesString) {
  throw new Error("Missing required input data (Service, State, Country, or City list)");
}

// Split cities string by newline and clean extra spaces
const cities = citiesString.split('\n').map(city => city.trim()).filter(city => city.length > 0);

// Generate output items dynamically for each city
const output = cities.map(city => ({
  json: {
    name: serviceName,
    country: country,
    state: state,
    city: city,
    category: serviceName
  }
}));

return output;

const items = $input.all();

const seen = new Map();
const duplicates = [];

for (const item of items) {
  const businessName = String(item.json['Business Name'] || '').trim().toLowerCase();
  const phoneNumber = String(item.json['Phone Number'] || '').trim();
  const address = item.json['Address'];
  const mapsUrl = item.json['Google Maps URL'];

  if (!businessName) continue;

  const key = businessName;

  if (seen.has(key)) {
    const previous = seen.get(key);

    if (!phoneNumber || phoneNumber === previous.phoneNumber) {
      duplicates.push({
        json: {
          Address: address,
          'Google Maps URL': mapsUrl
        }
      });
    }

  } else {
    seen.set(key, { phoneNumber });
  }
}

return duplicates;
