// Node name: Join Pages
// 1. Grab all incoming documents
const docs = $input.all();

// 2. Pull in your schema once and filter it
let schema = $('extract schema').first().json.schema;
const toRemove = ['Id', 'row_number'];
schema = schema.filter(f => !toRemove.includes(f));

// 3. Build one output per document
const results = docs.map(doc => {
  // join only that doc‚Äôs pages
  const fullText = doc.json.pages
    .map(p => p.markdown)
    .join('\n\n');

  return {
    json: {
      ocr_text: fullText,
      schema,
    },
  };
});

return results;

// 1. Get the array of processed files (with id & name)
const processed = $('Filter processed files').all();

// 2. Take each LLM output in turn, parse it, inject the matching ID, and return
return $input.all().map((item, index) => {
  // parse the JSON string your chat node returned
  const data = JSON.parse(item.json.output);

  // look up the corresponding Drive file ID by array index
  data.Id   = processed[index]?.json.id ?? null;


  // emit for your Sheets node
  return { json: data };
});
