const mimeType = $('Incoming request').item.json.body.message.document.mime_type;

let file_class = 'false';

if (mimeType === 'image/jpeg' || mimeType === 'image/png') {
  file_class = 'image_url';
} else if (mimeType === 'application/pdf') {
  file_class = 'document_url';
}

return [{
  json: {
    file_class
  }
}];

// Get data from the previous block
const inputData = items[0].json;

// Validate input data
if (!inputData.pages || !Array.isArray(inputData.pages)) {
  throw new Error('Invalid input: pages is missing or not an array');
}

// Format page-by-page markdown text
const markdownPages = inputData.pages.map((page, index) => {
  if (typeof page.markdown !== 'string') {
    throw new Error(`Invalid input: markdown is missing or not a string on page ${index + 1}`);
  }
  return `# Page ${index + 1}\n\n${page.markdown}`;
});

// Combine pages with a convenient separator
const finalText = markdownPages.join('\n\n---\n\n');

// Return data for the next block
return [{
  json: {
    fileName: 'output_markdown.txt',
    fileContent: finalText
  }
}];

// Get Chat_ID from the incoming Telegram message
const chatId = String($('Settings').item.json.Chat_ID);

// Get allowed Chat IDs string from 'Settings' node
const allowedChatIdsString = $('Settings').item.json.Allowed_Chat_IDs;

// Initialize allowedChatIds as an empty array
let allowedChatIds = [];

// Process allowedChatIdsString only if it's a non-empty string
if (typeof allowedChatIdsString === 'string' && allowedChatIdsString.trim() !== '') {
  // Split string by comma, map to string, and trim whitespace from each ID
  allowedChatIds = allowedChatIdsString.split(',').map(id => String(id).trim());

  // Filter out any empty strings that might result from multiple commas (e.g., "1, ,2")
  allowedChatIds = allowedChatIds.filter(id => id !== '');
}

// If allowedChatIds is empty after processing, it means no specific IDs are whitelisted.
// The regex will correctly handle an empty array (it will become '^(?:)$' matching nothing).

// Escape special regex characters in IDs to prevent errors and ensure literal matching
const escapedChatIds = allowedChatIds.map(id => id.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'));

// Construct regex for full string match.
// If escapedChatIds is empty, the regex will be '^(?:)$', which correctly matches nothing.
const regex = new RegExp(`^(?:${escapedChatIds.join('|')})$`);

// Check if chatId is in the whitelist
const isWhitelisted = regex.test(chatId);

// Return the result
return [{ json: { isWhitelisted: isWhitelisted } }];
