// Get the Knowledge Base data from the Notion node
const kbPages = $input.all().map(item => item.json);

// Helper function to extract text from Notion properties
function extractText(property) {
  if (!property) return '';

  // Handle rich_text properties
  if (property.rich_text && property.rich_text.length > 0) {
    return property.rich_text[0].plain_text || '';
  }

  // Handle title properties
  if (property.title && property.title.length > 0) {
    return property.title[0].plain_text || '';
  }

  // Handle select properties
  if (property.select) {
    return property.select.name || '';
  }

  // Handle number properties
  if (property.number) {
    return property.number.toString();
  }

  return '';
}

// Create a linear array of knowledge base entries
const knowledgeArray = kbPages.map(page => {
  const properties = page.properties || {};

  // Extract information based on your actual Notion properties
  const productName = page.property_products || '';
  const price = page.property_prices || '';
  const description = page.property_description || '';
  const name = page.name || '';

  // Create a formatted knowledge entry
  const knowledgeEntry = {
    name: name,
    product: productName,
    price: price,
    description: description,
    // Create a searchable text string
    searchableText: [name, productName, price, description]
      .filter(Boolean)
      .join(' | ')
  };

  return knowledgeEntry;
}).filter(entry => entry.name || entry.product); // Only include entries with names or products

// Create a simple string array for easy searching
const knowledgeStrings = knowledgeArray.map(entry => 
  `NAME: ${entry.name || 'N/A'}` +
  (entry.product ? ` | PRODUCT: ${entry.product}` : '') +
  (entry.price ? ` | PRICE: ${entry.price}` : '') +
  (entry.description ? ` | DESCRIPTION: ${entry.description}` : '')
);

// Output the processed data
return [{
  json: {
    // Original data passthrough
    ...($input.first()?.json || {}),

    // Structured knowledge base data
    knowledgeBase: {
      entries: knowledgeArray,
      searchableStrings: knowledgeStrings,
      totalProducts: knowledgeArray.length
    },

    // Simple concatenated string for memory context
    knowledgeContext: knowledgeStrings.join('\n\n'),

    // Alternative: Create a more concise version for memory
    compactKnowledge: knowledgeArray.map(entry => 
      `${entry.name || entry.product}: ${entry.description || 'No description'} (${entry.price || 'Price not available'})`
    ).join('\n')
  }
}];

