// Loop through incoming items
return items.map(item => {
  const part = item.json.candidates[0]?.content?.parts.find(p => p.inlineData);
  if (!part || !part.inlineData) return item;

  const base64 = part.inlineData.data;
  return {
    json: {}, // optionally pass through other json fields
    binary: {
      data: {
        data: base64,
        mimeType: part.inlineData.mimeType || 'image/png',
        fileName: 'gemini_image.png',
        fileExtension: part.inlineData.mimeType?.split('/')[1] || 'png'
      }
    }
  };
});
