// Get the original list from the 'Request' key in the incoming data.
const originalList = $input.first().json.output.Matched_News;

// Create a new array to store the formatted output.
const outputItems = [];

// Loop through each item in the original list.
for (const item of originalList) {
  // For each item, create a new object in the desired format.
  outputItems.push({
    json: {
      "text": item
