const results = [];

for (const input of $input.all()) {
  const response = input.json;
  const itemsArray = response.items || [];
  const searchTerms = response.queries?.request?.[0]?.searchTerms || null;
  const nextPageStartIndex = response.queries?.nextPage?.[0]?.startIndex || null;

  for (const item of itemsArray) {
    const metatags = item?.pagemap?.metatags?.[0] || {};
    results.push({
      json: {
        title: item.title || null,
        link: item.link || null,
        searchTerms: searchTerms,
        nextPageStartIndex: nextPageStartIndex,
        snippet: item.snippet || null,
        ogDescription: metatags["og:description"] || null,
        twitterImage: metatags["twitter:image"] || null
      }
    });
  }
}

return results;
