let idx = Number($json.output.best_image_index);
let url = $('set_common_fields').item.json.tool_url;
let domain = $('set_common_fields').item.json.domain;
let icon_url = "";

switch (idx) {
  // google
  case 0:
    icon_url = `https://t3.gstatic.com/faviconV2?client=SOCIAL&type=FAVICON&fallback_opts=TYPE,SIZE,URL&url=${url}&size=256`;
    break;

  // logo.dev
  case 1:
    icon_url = `https://img.logo.dev/${domain}?token=pk_b6ax6oVTTlSLqCezmTitOg&format=png&size=512`;
    break;

  // clearbit
  case 2:
    icon_url = `https://logo.clearbit.com/${domain}?size=256&format=png`;
    break;

  default:
    throw new Error("Unknown image index");
}

return { icon_url };
