const input = items[0].json;
const posts = input.items || [];

const extracted = posts.map(post => {
  const quoted = post.quoted_post || {};
  const taggedUsers = post.tagged_users || [];

  return {
    date_posted: post.date_posted || null,
    description: post.description || null,
    hashtags: post.hashtags || [],
    likes: post.likes || null,

    quoted_date_posted: quoted.date_posted || null,
    quoted_description: quoted.description || null,

    quotes: post.quotes || 0,
    replies: post.replies || 0,
    reposts: post.reposts || 0,

    profile_names: taggedUsers.map(user => user.profile_name || null),

    views: post.views || 0,
  };
});

return [
  {
    json: {
      posts: extracted
    }
  }
];

