// Get the response from Notion API
const notionResponse = $input.first().json;
const pages = notionResponse.results || [];

// Helper function to extract text from Notion properties
function extractText(property) {
  if (!property) return '';

  switch (property.type) {
    case 'title':
      return property.title?.map(t => t.plain_text).join('\n') || '';
    case 'rich_text':
      return property.rich_text?.map(t => t.plain_text).join('\n') || '';
    case 'select':
      return property.select?.name || '';
    case 'multi_select':
      return property.multi_select?.map(item => item.name).join(', ') || '';
    case 'number':
      return property.number?.toString() || '';
    case 'checkbox':
      return property.checkbox ? '[YES_TEXT]' : '[NO_TEXT]';
    case 'date':
      return property.date?.start || '';
    case 'email':
      return property.email || '';
    case 'phone_number':
      return property.phone_number || '';
    case 'url':
      return property.url || '';
    default:
      return '';
  }
}

// Process data into clean JSON
const database = pages.map(page => {
  const item = {};
  for (const [propName, propData] of Object.entries(page.properties)) {
    item[propName] = extractText(propData);
  }
  return item;
}).filter(item => Object.values(item).some(value => value.length > 0));

// Build databaseText (preserve newlines inside values; do NOT collapse \n)
const databaseText = database.map(item => {
  return Object.entries(item)
    .filter(([key, value]) => value && value.trim() !== '')
    .map(([key, value]) => {
      let clean = value
        .replace(/[\"""¬´¬ª‚Äû]/g, '')   // remove quotes
        .replace(/[ \t]+/g, ' ')    // collapse spaces/tabs only; keep \n
        .replace(/\r\n/g, '\n')     // normalize Windows newlines
        .trim();
      return `${key}: ${clean}`;
    })
    .join(' | ');
}).join('\n\n');

// Build databaseRaw (keeps full formatting for Messenger DMs)
const databaseRaw = database.map(item => {
  return Object.entries(item)
    .filter(([key, value]) => value && value.trim() !== '')
    .map(([key, value]) => `${key}: ${value}`)
    .join('\n');
}).join('\n\n');

// Build databaseRawList (array per entry, verbatim)
const databaseRawList = database.map(item => {
  return Object.entries(item)
    .filter(([key, value]) => value && value.trim() !== '')
    .map(([key, value]) => `${key}: ${value}`)
    .join('\n');
});

// Return multiple formats
return [{
  json: {
    database,                                   // Original array (for processing)
    databaseJSON: JSON.stringify(database, null, 2), // Pretty formatted JSON string
    databaseText,                               // Cleaned & flattened text (for AI), keeps \n
    databaseRaw,                                // Raw text with formatting (for DM)
    databaseRawList,                            // Array of verbatim entries
    databaseString: JSON.stringify(database)    // Compact JSON string
  }
}];

const fullId = $json.comment_id;
return [{
  json: {
    ...$json,
    comment_id_raw: fullId.split('_').pop()  // keep only the last part
  }
}];
