const items = $input.all();
const updatedItems = items.map((item) => {
  let html = item?.json?.data;

  // Trim to body
  let bodyStart = html.indexOf("<body>");
  let bodyEnd = html.indexOf("</body>") + 7;
  html = html.slice(bodyStart, bodyEnd);

  // Remove header
  html = html.replace(/<header>[\s\S]*?<\/header>/g, "");

  // Remove scripts
  html = html.replace(/<script>[\s\S]*?<\/script>/g, "");

  // Remove iframes
  html = html.replace(/<iframe[\s\S]*?<\/iframe>/g, "");

  item.json.data = html;
  return item;
});

return updatedItems;
