let results = [];

for (let item of items) {
    if (item.binary) {
        // If there's binary data in the item, process each binary file
        for (let key in item.binary) {
            // Use the key as the file name
            let binaryKey = key.replace(/\s/g, 'Upload_Resume_PDF'); // Replace spaces with underscores for the key
            results.push({
                json: {
                    fileName: binaryKey
                },
                binary: {
                    [binaryKey]: item.binary[key] // Use the modified key for the binary data
                }
            });
        }
    }
}

return results;

// Get the response from Perplexity
const response = $json.message || $json.content || $json.text;

// Extract content between <html> and </html> tags
const htmlMatch = response.match(/<html[\s\S]*?<\/html>/i);

if (htmlMatch) {
  // Found HTML content - now remove all \n characters
  const cleanHtml = htmlMatch[0].replace(/\n/g, '');

  return [{
    json: {
      cleanedResponse: cleanHtml,
      originalResponse: response
    }
  }];
} else {
  // No HTML found, return original
  return [{
    json: {
      cleanedResponse: response,
      originalResponse: response,
      error: "No HTML tags found in response"
    }
  }];
}
