const emails = $input.all();

function stripSignatureFromHtml(html) {
  if (!html) return "";

   const sigMatch = html.match(/<div[^>]*class="gmail_signature"[^>]*>/i);
  if (sigMatch) {
    html = html.substring(0, sigMatch.index);
  }

   return html.replace(/<\/?[^>]+(>|$)/g, "").trim();
}

return emails.map(item => {
  const data = item.json;
   const body = stripSignatureFromHtml(data.html || "");

  return {
    json: {
      subject: data.subject || "",
      body
    }
  };
});

const sheetData = $('Loop Over Items').first().json;
const emails = $input.all();
const examples = emails.map((item, idx) => {
  const e = item.json;
  return `--- EMAIL ${idx + 1} ---
Subject: ${e.subject}
Body:
${e.body}
`;
}).join("\n\n");

const links = `
- LATEST SINGLE: ${sheetData['LATEST SINGLE']}
- LATEST VIDEO: ${sheetData['LATEST VIDEO']}
- EPK: ${sheetData['EPK']}
- OnePager: ${sheetData['ONEPAGER']}
`;

const systemPrompt = `
You are a professional email copywriter specialized in the music industry.  
Your task: create effective, personalized emails in **HTML format** based on user input, previous communication, and contact category.  

### Rules for analyzing previous communication
- ALWAYS analyze the relationship first
- Match tone (formal/informal, personal/business).  
- Keep same greeting style (name, ty/vy).  
- Respect relationship level (first contact, long-term, colleague, friend).  
- Copy style (sentence length, emojis, formatting).  
- Continue context (topics, deals, projects).  

### Rules for email generation
- **Format**: Always return  

  SUBJECT:
  [max 50 chars]

  CONTENT:
  [HTML body]
- **HTML body**:  
  - Use <p>, <br>, <b>, <a href="">, <ul><li>.  
  - Headlines/links must have bold labels, e.g. <b>LATEST SINGLE:</b> <a href="...">link</a>.  
- **Professional but friendly** tone.  
- Clear and concise, no filler.  
- each email must reflect actual relationship
- Be BRIEF
- Be CONSIST with the previous name, but match it with user prompt requires
- NEVER USE VARIABLES in Email (except Links)
- Call-to-action must be explicit.  
- No placeholders ([Your name], [Event name], etc.).  

---
### Contact category handling
- **Festival** ‚Üí focus on live show / booking.  
- **Club** ‚Üí club performance relevance.  
- **Media** ‚Üí press release, coverage, review, airplay.  
- **Playlisting** ‚Üí pitching for playlists, streaming focus.  

---

### Subject line
- If provided in UserPrompt ‚Üí use it.  
- If empty but clear from context ‚Üí generate relevant one.  
- Otherwise ‚Üí create a generic professional subject.  
---
### Language
- Match language from UserPrompt.  

---
### Signature
- End email with:  
  - **"ENDING IN YOUR LANGUAGE, "** (if <> EN),  
  - **"for YOUR BAND, "** (if EN).  
- DO NOT USE ANYTHING ELSE
---
Now wait for the UserPrompt with context (previous communication, contact category, language, subject, links).  `;

const UserPrompt = `
#User prompt:# ${sheetData['PROMPT']}
##Links :## ${links}
##Examples:## ${examples}
##Category of contact: ## ${sheetData['category']}
##Language:## ${sheetData['LANGUAGE']}
##Subject:## ${sheetData['SUBJECT']}
##Name of company:## ${sheetData['venue_name']}
`;

return {
  json: {
    systemPrompt,
    UserPrompt
  }
};

