// La sortie de l'Agent IA (en mode String) est dans la propri√©t√© 'output'.
const rawText = $input.item.json.output;

// V√©rification de robustesse : s'assurer que rawText existe avant de le traiter.
if (typeof rawText !== 'string' || rawText.trim() === '') {
  // Si rawText n'est pas une cha√Æne de caract√®res ou est vide, l'Agent IA a probablement √©chou√©.
  console.error("L'entr√©e de l'Agent IA est manquante ou n'est pas une cha√Æne de caract√®res.", $input.item.json);
